INSERT INTO `page_statics` (`id`, `ps_name`, `ps_type`, `ps_content`, `created_at`, `updated_at`) VALUES
(1, 'Về chúng tôi', 1, '<p><strong>C&ocirc;ng ty Cổ phần Đầu tư Thế Giới Di Động (MWG)</strong>&nbsp;l&agrave;&nbsp;<strong>nh&agrave; b&aacute;n lẻ số 1 Việt Nam</strong>&nbsp;về doanh thu v&agrave; lợi nhuận, với&nbsp;mạng lưới&nbsp;hơn 2200 cửa h&agrave;ng tr&ecirc;n to&agrave;n quốc. MWG vận h&agrave;nh&nbsp;c&aacute;c chuỗi b&aacute;n lẻ thegioididong.com, Điện M&aacute;y Xanh,&nbsp;B&aacute;ch Ho&aacute; Xanh. Ngo&agrave;i ra, MWG&nbsp;đ&atilde; mở rộng ra thị trường nước ngo&agrave;i với chuỗi b&aacute;n lẻ Bigphone tại Campuchia.</p>\r\n\r\n<p><a href=\"https://thegioididong.com/\"><img alt=\"\" height=\"32\" src=\"https://mwg.vn/wp-content/uploads/2018/06/tgdd.png\" width=\"136\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/\"><strong>Thegioididong.com</strong></a>&nbsp;được th&agrave;nh lập từ năm 2004, l&agrave; chuỗi&nbsp;b&aacute;n lẻ&nbsp;<strong>thiết bị di động</strong>&nbsp;(điện thoại di động, m&aacute;y t&iacute;nh bảng, laptop v&agrave; phụ kiện) c&oacute;&nbsp;<strong>thị phần&nbsp;số 1 Việt Nam</strong>&nbsp;với hơn 1000 cửa h&agrave;ng&nbsp;hiện diện tại 63 tỉnh th&agrave;nh tr&ecirc;n khắp Việt Nam.</p>\r\n\r\n<p>Website:&nbsp;<a href=\"https://www.thegioididong.com/\">www.thegioididong.com</a></p>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/\"><img alt=\"\" height=\"32\" src=\"https://mwg.vn/wp-content/uploads/2018/06/dmx.png\" width=\"131\" /></a></p>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/\"><strong>Điện M&aacute;y Xanh</strong></a>, tiền th&acirc;n l&agrave;&nbsp;chuỗi&nbsp;Dienmay.com&nbsp;ra đời&nbsp;cuối 2010, l&agrave; chuỗi&nbsp;b&aacute;n lẻ c&aacute;c sản phẩm&nbsp;<strong>điện tử ti&ecirc;u d&ugrave;ng</strong>&nbsp;(điện tử, điện lạnh v&agrave; gia dụng) c&oacute;&nbsp;<strong>thị phần số 1 Việt Na</strong>m với hơn 800&nbsp;cửa h&agrave;ng hiện diện tại 63 tỉnh th&agrave;nh tr&ecirc;n khắp Việt Nam.</p>\r\n\r\n<p>Website:&nbsp;<a href=\"https://www.dienmayxanh.com/\">www.dienmayxanh.com</a></p>\r\n\r\n<p><a href=\"https://www.bachhoaxanh.com/\"><img alt=\"\" height=\"32\" src=\"https://mwg.vn/wp-content/uploads/2018/06/bhx.png\" width=\"139\" /></a></p>\r\n\r\n<p><a href=\"https://www.bachhoaxanh.com/\"><strong>B&aacute;ch H&oacute;a Xanh&nbsp;</strong></a>được đưa v&agrave;o thử nghiệm cuối năm 2015, l&agrave; chuỗi cửa h&agrave;ng&nbsp;chuy&ecirc;n b&aacute;n lẻ<strong>&nbsp;thực phẩm tươi sống (thịt c&aacute;, rau củ, tr&aacute;i c&acirc;y&hellip;) v&agrave; nhu yếu phẩm</strong>&nbsp;với hơn 500 si&ecirc;u thị tại TPHCM v&agrave; c&aacute;c tỉnh Nam Bộ. B&aacute;ch Ho&aacute; Xanh cung cấp những sản phẩm thiết yếu h&agrave;ng ng&agrave;y tươi ngon, nguồn gốc r&otilde; r&agrave;ng, đa dạng về chủng loại; gi&aacute; cả hợp l&yacute; so với chợ truyền thống v&agrave; c&aacute;c cửa h&agrave;ng nhỏ lẻ; trong một kh&ocirc;ng gian sạch sẽ, th&acirc;n thiện v&agrave; tại những địa điểm dễ tiếp cận đối với người nội trợ.</p>\r\n\r\n<p>Website:&nbsp;<a href=\"https://www.bachhoaxanh.com/\">www.bachhoaxanh.com</a></p>\r\n\r\n<p><a href=\"https://www.bigphone.com/\"><img alt=\"\" height=\"32\" src=\"https://mwg.vn/wp-content/uploads/2019/04/Bigphone.png\" width=\"136\" /></a></p>\r\n\r\n<p><strong><a href=\"https://www.bigphone.com/\">BigPhone</a></strong>&nbsp;l&agrave;&nbsp;<strong>chuỗi b&aacute;n lẻ thiết bị di động ở thị trường nước ngo&agrave;i</strong>&nbsp;của MWG được đưa v&agrave;o thử nghiệm với si&ecirc;u thị đầu ti&ecirc;n tại Campuchia cuối th&aacute;ng 6/2017. Bigphone hiện c&oacute; 10 cửa h&agrave;ng tập trung tại thủ đ&ocirc; Phn&ocirc;m P&ecirc;nh v&agrave; l&agrave; đơn vị b&aacute;n lẻ chuy&ecirc;n thiết bị di động ch&iacute;nh h&atilde;ng đầu ti&ecirc;n tại Campuchia. Bigphone mang đến cho kh&aacute;ch h&agrave;ng nơi mua sắm h&agrave;ng ho&aacute; đ&aacute;ng tin cậy với dịch vụ hậu m&atilde;i chất lượng v&agrave; kết hợp với c&aacute;c tổ chức t&agrave;i ch&iacute;nh ti&ecirc;u d&ugrave;ng để b&aacute;n h&agrave;ng trả g&oacute;p, những điều m&agrave; người ti&ecirc;u d&ugrave;ng Campuchia chưa hề được trải nghiệm khi mua tại cửa h&agrave;ng nhỏ lẻ.</p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-17px; position:absolute; top:38px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', '2019-11-18 09:20:58', '2019-11-18 10:16:05'),
(2, 'Thông tin giao hàng', 2, '<p>I. Giao h&agrave;ng nhanh: Giao h&agrave;ng tận nơi miễn ph&iacute; từ 30 ph&uacute;t - 2 tiếng</p>\r\n\r\n<h4>1. Phạm vi &aacute;p dụng</h4>\r\n\r\n<p>Những khu vực tỉnh th&agrave;nh c&oacute;&nbsp;<a href=\"https://www.thegioididong.com/\" target=\"_top\" title=\"hệ thống thegioididong\">hệ thống si&ecirc;u thị Thegioididong.com</a>.</p>\r\n\r\n<h4>2. Thời gian nhận h&agrave;ng</h4>\r\n\r\n<p>Thegioididong nhận giao nhanh trong ng&agrave;y với khoảng c&aacute;ch từ c&aacute;c si&ecirc;u thị c&oacute; h&agrave;ng đến điểm giao l&agrave; 20 km. Khoảng c&aacute;ch lớn hơn nh&acirc;n vi&ecirc;n của ch&uacute;ng t&ocirc;i sẽ tư vấn c&aacute;ch thức giao h&agrave;ng thuận tiện nhất cho kh&aacute;ch h&agrave;ng. Cụ thể:</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" dir=\"ltr\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Khoảng c&aacute;ch đến nh&agrave; kh&aacute;ch từ si&ecirc;u thị gần nhất c&oacute; h&agrave;ng</td>\r\n			<td>TP. HCM</td>\r\n			<td>Tỉnh kh&aacute;c</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Dưới 5km</td>\r\n			<td>Giao trong 30 ph&uacute;t miễn ph&iacute;</td>\r\n			<td>Giao trong&nbsp;30 ph&uacute;t&nbsp;miễn ph&iacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>5-10km</td>\r\n			<td>Giao trong 1 tiếng miễn ph&iacute;</td>\r\n			<td>Giao trong 1 tiếng miễn ph&iacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>10-20km</td>\r\n			<td>Giao trong 2 tiếng miễn ph&iacute;</td>\r\n			<td>Giao trong 2 tiếng miễn ph&iacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Lưu &yacute;</td>\r\n			<td>Thời gian giao h&agrave;ng 9:00 đến 21:00</td>\r\n			<td>Thời gian giao h&agrave;ng 9:00 đến 20:00</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>+ Đối với h&agrave;ng&nbsp;<a href=\"https://www.thegioididong.com/phu-kien\">Phụ kiện</a>,&nbsp;<a href=\"https://www.thegioididong.com/sim-so-dep\">Sim số</a>&nbsp;&amp;&nbsp;<a href=\"https://www.thegioididong.com/dtdd\">Điện thoại</a>&nbsp;gi&aacute; trị gi&aacute;&nbsp;<strong>dưới&nbsp;200.000đ</strong>, ph&iacute; giao h&agrave;ng &aacute;p dụng:&nbsp;<strong>10.000đ.</strong></p>\r\n\r\n<p>+ Nếu khoảng c&aacute;ch giao h&agrave;ng lớn hơn 20 km tổng đ&agrave;i&nbsp;<strong>1800 1060</strong>&nbsp;sẽ tư vấn c&aacute;ch thức giao h&agrave;ng&nbsp;<strong>MIỄN PH&Iacute;</strong>&nbsp;thuận tiện nhất cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>+ Đối với&nbsp;<a href=\"https://www.thegioididong.com/laptop\">Laptop</a>&nbsp;kh&aacute;ch h&agrave;ng cần cọc 200.000đ để chuyển h&agrave;ng, miễn ph&iacute; chuyển &amp; giao h&agrave;ng.</p>\r\n\r\n<h4>3. Được lựa chọn sản phẩm tại nh&agrave; (2 mẫu):</h4>\r\n\r\n<p>Nếu c&oacute; sự băn khoăn trong việc lựa chọn sản phẩm, h&atilde;y để nh&acirc;n vi&ecirc;n giao h&agrave;ng của ch&uacute;ng t&ocirc;i sẽ đem hơn 2 sản phẩm theo y&ecirc;u cầu của bạn đến tận nơi tư vấn.</p>\r\n\r\n<p>II. Chuyển h&agrave;ng to&agrave;n quốc miễn ph&iacute;</p>\r\n\r\n<p>Thegioididong.com chuyển h&agrave;ng h&oacute;a từ khu vực kh&aacute;c đến tận nơi kh&aacute;ch h&agrave;ng y&ecirc;u cầu tr&ecirc;n to&agrave;n quốc miễn ph&iacute;. Thời gian vận chuyển tối đa 3 ng&agrave;y (&aacute;p dụng với khu vực: TPHCM, H&agrave; Nội, Đ&agrave; Nẵng), c&aacute;c khu vực kh&aacute;c thời gian vận chuyển tối đa l&agrave; 5 ng&agrave;y. Thời gian được t&iacute;nh từ l&uacute;c ho&agrave;n tất thủ tục đặt h&agrave;ng với tổng đ&agrave;i vi&ecirc;n đến khi nhận được h&agrave;ng.</p>\r\n\r\n<p>(đối với đơn h&agrave;ng sim số c&oacute; gi&aacute; dưới 100.000 sẽ t&iacute;nh ph&iacute; giao hoặc chuyển l&agrave; 20.000đ)</p>\r\n\r\n<p>III. Ch&iacute;nh s&aacute;ch giao h&agrave;ng đối với phụ kiện ch&iacute;nh h&atilde;ng</p>\r\n\r\n<p>+ Khu vực TP. Hồ Ch&iacute; Minh : Giao h&agrave;ng từ 1-3 ng&agrave;y</p>\r\n\r\n<p>+ Khu vực tỉnh: Giao h&agrave;ng từ 2-7 ng&agrave;y</p>\r\n\r\n<p>Lưu &yacute;: Đối với kh&aacute;ch h&agrave;ng ở tỉnh hoặc c&aacute;ch si&ecirc;u thị&nbsp;<a href=\"https://www.thegioididong.com/sieu-thi-330-cong-hoa-p13-qtan-binh-tphcm\" target=\"_blank\" title=\"Thế Giới Di Động 330 Cộng Hoà\" type=\"Thế Giới Di Động 330 Cộng Hoà\">Thế Giới Di Động 330 Cộng Ho&agrave;</a>&nbsp;tr&ecirc;n 20 Km. &nbsp;Kh&aacute;ch h&agrave;ng thanh to&aacute;n 100% gi&aacute; trị sản phẩm tại si&ecirc;u thị gần nhất hoặc thanh to&aacute;n Online</p>\r\n\r\n<p>Trải nghiệm dịch vụ mua sắm ho&agrave;n hảo</p>\r\n\r\n<p><img alt=\"\" src=\"https://thegioididong.com/Content/images/dummies/ship1.jpg\" width=\"400px\" /></p>\r\n\r\n<p>Khi c&oacute; nhu cầu về th&ocirc;ng tin hoặc mua sắm sản phẩm. H&atilde;y gọi cho ch&uacute;ng t&ocirc;i qua số&nbsp;<strong>1800.1060</strong>. Ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng phục vụ từ 7h30 đến 22h.</p>\r\n\r\n<p><img alt=\"\" src=\"https://thegioididong.com/Content/images/dummies/ship2.jpg\" width=\"400px\" /></p>\r\n\r\n<p>Nh&acirc;n vi&ecirc;n tư vấn giao h&agrave;ng sẽ kiểm tra thật kỹ v&agrave; lựa chọn một số sản phẩm m&agrave; Qu&yacute; kh&aacute;ch đang quan t&acirc;m để Qu&yacute; kh&aacute;ch c&oacute; nhiều lựa chọn hơn.</p>\r\n\r\n<p>Trong v&ograve;ng 2 - 4 tiếng đồng hồ ch&uacute;ng t&ocirc;i sẽ c&oacute; mặt tại nơi Qu&yacute; kh&aacute;ch y&ecirc;u cầu.</p>\r\n\r\n<p><img alt=\"\" src=\"https://thegioididong.com/Content/images/dummies/ship4.jpg\" width=\"400px\" /></p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn nhiều hơn 1 sản phẩm.</p>\r\n\r\n<p><img alt=\"\" src=\"https://thegioididong.com/Content/images/dummies/ship5.jpg\" width=\"400px\" /></p>\r\n\r\n<p>Kỹ thuật vi&ecirc;n của ch&uacute;ng t&ocirc;i sẽ gi&uacute;p Qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute; kỹ hơn những t&iacute;nh năng ưu việt của từng sản phầm để Qu&yacute; kh&aacute;ch c&oacute; được lựa chọn tốt nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"https://thegioididong.com/Content/images/dummies/ship6.jpg\" width=\"400px\" /></p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch chỉ thanh to&aacute;n khi thật sự h&agrave;i l&ograve;ng với sản phẩm v&agrave; chất lượng dịch vụ của ch&uacute;ng t&ocirc;i. Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng t&iacute;nh bất kỳ khoản ph&iacute; n&agrave;o cho đến khi Qu&yacute; kh&aacute;ch ho&agrave;n to&agrave;n đồng &yacute;.&nbsp;</p>\r\n\r\n<p>Khi Qu&yacute; kh&aacute;ch ho&agrave;n to&agrave;n h&agrave;i l&ograve;ng với sự lựa chọn của m&igrave;nh, Qu&yacute; kh&aacute;ch c&oacute; thể thanh to&aacute;n ngay bằng c&aacute;c thẻ quốc tế, nội địa m&agrave; kh&ocirc;ng cần phải ra ng&acirc;n h&agrave;ng r&uacute;t tiền mặt trước.</p>\r\n\r\n<p>H&atilde;y gọi cho ch&uacute;ng t&ocirc;i bất cứ l&uacute;c n&agrave;o Qu&yacute; kh&aacute;ch cần được phục vụ với chất lượng 5 sao ho&agrave;n hảo.</p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-33px; position:absolute; top:-6px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', '2019-11-18 09:22:05', '2019-11-18 10:13:29');
INSERT INTO `page_statics` (`id`, `ps_name`, `ps_type`, `ps_content`, `created_at`, `updated_at`) VALUES
(3, 'Chính sách bảo hành', 3, '<h1 style=\"text-align:start\"><span style=\"color:#666666\"><strong>I. SẢN PHẨM MỚI:<br />\r\n<br />\r\nĐỐI VỚI ĐIỆN THOẠI, M&Aacute;Y T&Iacute;NH BẢNG, LAPTOP, ĐỒNG HỒ TH&Ocirc;NG MINH, LOA K&Eacute;O</strong></span></h1>\r\n\r\n<table style=\"-webkit-text-stroke-width:0px; background:#ffcc99; border-radius:5px; border:1px; color:#333333; font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif; font-size:14px; font-style:normal; font-variant-caps:normal; font-variant-ligatures:normal; font-weight:400; letter-spacing:normal; margin-bottom:5px; margin-top:5px; orphans:2; padding:0px; text-align:start; text-decoration-color:initial; text-decoration-style:initial; text-indent:0px; text-transform:none; white-space:normal; widows:2; width:100%; word-spacing:0px\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\"><span style=\"color:#000000\"><strong>Sản phẩm lỗi do nh&agrave; sản xuất:</strong></span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px; width:51%px\"><strong>Th&aacute;ng 1</strong></td>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; width:49%px\">\r\n			<p><strong>Th&aacute;ng 2 - 12</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; vertical-align:top\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\">\r\n			<p><strong>- 1 đ&ocirc;̉i 1 (cùng mẫu, cùng màu, cùng dung lượng...)</strong>.<br />\r\n			-&nbsp;Trường hợp sản phẩm đổi hết h&agrave;ng, kh&aacute;ch h&agrave;ng c&oacute; thể đổi sang sản phẩm kh&aacute;c c&ugrave;ng nh&oacute;m h&agrave;ng c&oacute; gi&aacute; trị lớn hơn 50% gi&aacute; trị sản phẩm lỗi (<em>Thegioididong.com</em>&nbsp;sẽ ho&agrave;n tiền phần ch&ecirc;nh lệch cho kh&aacute;ch h&agrave;ng).<br />\r\n			<strong>Hoặc:</strong></p>\r\n\r\n			<p>Kh&aacute;ch h&agrave;ng trả m&aacute;y &amp;<em>&nbsp;Thegioididong.com</em>&nbsp;ho&agrave;n lại tiền với mức gi&aacute; bằng 80% gi&aacute; tr&ecirc;n ho&aacute; đơn.</p>\r\n			</div>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; vertical-align:top\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\">\r\n			<p>Gửi m&aacute;y bảo h&agrave;nh theo quy định của h&atilde;ng.</p>\r\n\r\n			<p><strong>Hoặc:</strong><br />\r\n			Kh&aacute;ch h&agrave;ng trả m&aacute;y &amp;<em>&nbsp;Thegioididong.com&nbsp;</em><strong>ho&agrave;n lại tiền v&agrave; thu ph&iacute; th&ecirc;m 5%</strong>&nbsp;so với mức ho&agrave;n tiền khi trả ở th&aacute;ng thứ 1.</p>\r\n\r\n			<p><strong>VD</strong>: Ở th&aacute;ng thứ nhất, nếu kh&aacute;ch h&agrave;ng trả sản phẩm sẽ được ho&agrave;n lại tiền với mức gi&aacute; bằng 80% th&igrave; sang th&aacute;ng thứ 2 nếu kh&aacute;ch h&agrave;ng trả m&aacute;y sẽ thu ph&iacute; th&ecirc;m 5% n&ecirc;n mức ho&agrave;n tiền sẽ c&ograve;n 75% gi&aacute; trị sản phẩm tr&ecirc;n ho&aacute; đơn, th&aacute;ng thứ 3 mức ho&agrave;n tiền sẽ trừ th&ecirc;m 5% th&agrave;nh 70%....</p>\r\n			</div>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\"><span style=\"color:#000000\"><strong>Sản phẩm kh&ocirc;ng lỗi (kh&ocirc;ng ph&ugrave; hợp với nhu cầu của kh&aacute;ch h&agrave;ng):</strong></span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px; width:51%px\"><strong>Th&aacute;ng 1</strong></td>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; width:49%px\">\r\n			<p><strong>Th&aacute;ng 2 - 12</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px; vertical-align:top; width:51%px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\">\r\n			<p>Ho&agrave;n lại tiền m&aacute;y với gi&aacute; bằng 80% gi&aacute; tr&ecirc;n ho&aacute; đơn.</p>\r\n			</div>\r\n			</td>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; vertical-align:top; width:49%px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\">\r\n			<p><strong>Ho&agrave;n lại tiền với mức ph&iacute; th&ecirc;m 5%</strong>&nbsp;so với th&aacute;ng thứ 1 (80%).&nbsp;<strong>VD</strong>: th&aacute;ng thứ 2 ho&agrave;n lại tiền với mức gi&aacute; 75% gi&aacute; tr&ecirc;n ho&aacute; đơn, th&aacute;ng thứ 3 l&agrave; 70%...</p>\r\n			</div>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px; vertical-align:top; width:51%px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\">\r\n			<p>C&ocirc;ng ty nhập lại sản phẩm cũ theo điều khoản &quot;trả lại h&agrave;ng&quot; (*) đồng thời xuất b&aacute;n lại sản phẩm mới.<br />\r\n			Phần ch&ecirc;nh lệch gi&aacute; l&agrave; khoản ph&iacute; sử dụng kh&aacute;ch h&agrave;ng phải trả v&agrave; c&ocirc;ng ty xuất ho&aacute; đơn gi&aacute; trị gia tăng (GTGT) cho khoản ph&iacute; n&agrave;y.</p>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\"><span style=\"color:#000000\"><strong>Sản phẩm lỗi do người sử dụng:</strong></span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px; vertical-align:top\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li>Kh&ocirc;ng đủ điều kiện bảo h&agrave;nh theo qui định của h&atilde;ng.</li>\r\n				<li>M&aacute;y kh&ocirc;ng giữ nguy&ecirc;n 100% h&igrave;nh dạng ban đầu.</li>\r\n				<li>M&agrave;n h&igrave;nh bị trầy xước.</li>\r\n			</ul>\r\n\r\n			<p>=&gt;&nbsp;<strong>Kh&ocirc;ng &aacute;p dụng bảo h&agrave;nh, đổi trả. Thegioididong.com hỗ trợ chuyển bảo h&agrave;nh, kh&aacute;ch h&agrave;ng chịu chi ph&iacute; sửa chữa.</strong></p>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<div style=\"-webkit-text-stroke-width:0px; padding:0px; text-align:start; text-indent:0px\">\r\n<h1><br />\r\n<span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"color:#666666\"><strong>ĐỐI VỚI ĐỒNG HỒ THỜI TRANG</strong></span></span></span></span></h1>\r\n\r\n<table style=\"background:#ffcc99; border-color:initial; border-radius:5px; border-style:initial; margin-bottom:5px; margin-top:5px; padding:0px; width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"color:#000000\"><strong>Sản phẩm lỗi do nh&agrave; sản xuất: (bao gồm c&aacute;c lỗi kĩ thuật li&ecirc;n quan đến bộ m&aacute;y, kh&ocirc;ng bao gồm pin)</strong><br />\r\n			<span style=\"color:#000000\"><span style=\"background-color:#ffcc99\">C&aacute;c trường hợp b&aacute;o lỗi phải chuyển Trung T&acirc;m Bảo H&agrave;nh thẩm định trước khi ra quyết định nhập đổi, nhập trả.</span></span></span></span></span></span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px; width:51%px\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><strong>Th&aacute;ng 1</strong></span></span></span></td>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; width:49%px\">\r\n			<p><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><strong>Th&aacute;ng 2 - 12</strong></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; vertical-align:top\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\">\r\n			<p><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"background-color:#ffffcc\"><strong>1.</strong>&nbsp;Lỗi 1 đổi 1 miễn ph&iacute; (C&ugrave;ng mẫu).</span><br />\r\n			<span style=\"font-size:10pt\"><span style=\"background-color:#ffffcc\"><span style=\"font-family:Arial\"><strong>2.</strong>&nbsp;Trường hợp sản phẩm Kh&aacute;ch H&agrave;ng đ&atilde; mua hết h&agrave;ng, Kh&aacute;ch h&agrave;ng (KH) được đổi qua mẫu kh&aacute;c (Kh&ocirc;ng mất ph&iacute;) (Thegioididong.com, DienmayXanh.com sẽ ho&agrave;n tiền phần ch&ecirc;nh lệch cho KH hoặc thu th&ecirc;m nếu c&oacute; ch&ecirc;nh lệch) HOẶC gửi bảo h&agrave;nh tại TTBH.</span></span></span><br />\r\n			<span style=\"font-size:10pt\"><span style=\"background-color:#ffffcc\"><span style=\"font-family:Arial\"><strong>3.</strong>&nbsp;Trường hợp KH mong muốn đổi mẫu kh&aacute;c (khi mẫu đ&atilde; mua vẫn c&ograve;n) hoặc KH mong muốn trả h&agrave;ng &amp; ho&agrave;n lại tiền, Thegioididong.com, DienmayXanh.com hỗ trợ KH như sau:&nbsp;&nbsp;</span></span></span><span style=\"font-size:10pt\"><span style=\"background-color:#ffffcc\"><span style=\"font-family:Arial\">Đổi sang sản phẩm kh&aacute;c, thu lại sản phẩm cũ hoặc ho&agrave;n tiền với mức 80% gi&aacute; tr&ecirc;n h&oacute;a đơn cũ.</span></span></span></span></span></span></p>\r\n			</div>\r\n			</td>\r\n			<td style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; vertical-align:top\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\">\r\n			<p><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffcc\"><strong>1.</strong>&nbsp;Gửi bảo h&agrave;nh sửa chữa tại TTBH theo quy định</span></span></span><br />\r\n			<span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\"><strong>2.</strong>&nbsp;Trường hợp kh&ocirc;ng sửa chữa được hoặc trả m&aacute;y bảo h&agrave;nh trễ hơn so với cam kết, KH được hỗ trợ đổi qua 1 sản phẩm mới hoặc ho&agrave;n tiền với mức ph&iacute; như sau:</span></span></span></span></span></p>\r\n\r\n			<p><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffcc\">- Đổi sang sản phẩm kh&aacute;c, thu lại sản phẩm cũ với mức ph&iacute; 25% cho th&aacute;ng thứ 2,</span></span></span><span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">v&agrave; cộng th&ecirc;m 5% mỗi th&aacute;ng tr&ecirc;n gi&aacute; h&oacute;a đơn (v&iacute; dụ th&aacute;ng thứ 2 l&agrave; 25%, th&aacute;ng thứ 3 l&agrave; 30%, th&aacute;ng thứ 4 l&agrave; 35% &hellip;.</span></span></span></span></span></p>\r\n\r\n			<p><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffcc\">- Ho&agrave;n tiền v&agrave; thu lại sản phẩm cũ với mức ph&iacute; 30% cho th&aacute;ng thứ 2,&nbsp;</span></span></span><span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffcc\">v&agrave; cộng th&ecirc;m 10% mỗi th&aacute;ng tr&ecirc;n gi&aacute; h&oacute;a đơn (v&iacute; dụ th&aacute;ng thứ 2 l&agrave; 30%, th&aacute;ng thứ 3 l&agrave; 40%, th&aacute;ng thứ 4 l&agrave; 50% &hellip;.</span></span></span></span></span></span></p>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"color:#000000\"><strong>Sản phẩm kh&ocirc;ng lỗi (kh&ocirc;ng ph&ugrave; hợp với nhu cầu của kh&aacute;ch h&agrave;ng): KH&Ocirc;NG HỖ TRỢ ĐỔI TRẢ</strong></span></span></span></span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"color:#000000\"><strong>Sản phẩm lỗi do người sử dụng:</strong></span></span></span></span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; border-top:1px solid #cccccc; height:32px; vertical-align:top\">\r\n			<div style=\"margin-left:5px; margin-right:5px; padding:0px\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\">-&nbsp;Hỗ trợ nhận m&aacute;y v&agrave; chuyển TTBH sửa chữa (c&oacute; t&iacute;nh ph&iacute;).</span></span></span></div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><strong>Lưu &yacute;:</strong><br />\r\n<span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\"><strong>Quy định về điều khoản thu lại h&agrave;ng của Thegioididong.com sẽ bao gồm:</strong>&nbsp;<strong>quy định về thời gian đổi trả, c&aacute;ch đổi trả, mức ho&agrave;n tiền &amp; mức ph&iacute; theo nội dung đ&atilde; cung cấp b&ecirc;n tr&ecirc;n, c&ugrave;ng với điều kiện đổi trả như sau:</strong></span></span><br />\r\n<br />\r\n<span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">-&nbsp;Đối với trường hợp sản phẩm kh&ocirc;ng giữ nguy&ecirc;n h&igrave;nh dạng ban đầu, bị trầy xước, cấp m&oacute;p sẽ thu ph&iacute; th&ecirc;m 10% tr&ecirc;n ho&aacute; đơn.</span></span><br />\r\n<br />\r\n<span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">- Tất cả TH KH b&aacute;o lỗi phải chuyển Trung T&acirc;m Bảo H&agrave;nh thẩm định trước khi ra quyết định nhập đổi, nhập trả.</span></span><br />\r\n<br />\r\n<span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">- C&ograve;n đầy đủ phiếu bảo h&agrave;nh (nếu c&oacute;) v&agrave; phụ kiện đi k&egrave;m (mất thu ph&iacute; theo qui định v&agrave; lớn nhất l&agrave; 5% tr&ecirc;n gi&aacute; ho&aacute; đơn, Mất hộp thu ph&iacute; 100.000đ). Ri&ecirc;ng đồng hồ&nbsp;thời trang&nbsp;MINIFOCUS, SKMEI, MEGIR, WEIDE, FONDERIA, H2X, Daniel Wellington&nbsp;kh&ocirc;ng cần phiếu bảo h&agrave;nh.</span></span><br />\r\n<br />\r\n<span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">- Qu&agrave; khuyến m&atilde;i: thu ph&iacute; theo gi&aacute; ho&agrave;n lại do TGDĐ c&ocirc;ng bố khi b&aacute;n sản phẩm. Nếu kh&ocirc;ng c&ocirc;ng bố gi&aacute; trị khuyến m&atilde;i th&igrave; sẽ thu ph&iacute; kh&ocirc;ng lớn hơn 5% gi&aacute; trị cho mỗi m&oacute;n qu&agrave; khuyến m&atilde;i .<br />\r\n<br />\r\n-&nbsp;</span></span><span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">Một số SP c&oacute; thời gian bảo h&agrave;nh tr&ecirc;n 12 th&aacute;ng, từ th&aacute;ng 13 trở đi sẽ được bảo h&agrave;nh tại TTBH</span></span><br />\r\n<br />\r\n<strong><span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">Đối với sản phẩm mua trả g&oacute;p:</span></span></strong><br />\r\n<br />\r\n<span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">-&nbsp;Trong 14 ng&agrave;y đầu ti&ecirc;n: huỷ hợp đồng, kh&ocirc;ng chịu ph&iacute;.</span></span><br />\r\n<br />\r\n<span style=\"font-size:13.3333px\"><span style=\"font-family:Arial\">- Sau 14 ng&agrave;y: qu&yacute; kh&aacute;ch phải thanh l&yacute; hợp đồng v&agrave; chịu ph&iacute; theo từng c&ocirc;ng ty trả g&oacute;p&nbsp; &nbsp;</span></span></span></span></span>\r\n\r\n<h1><br />\r\n<span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:Helvetica,Arial,&quot;DejaVu Sans&quot;,&quot;Liberation Sans&quot;,Freesans,sans-serif\"><span style=\"color:#666666\"><strong>ĐỐI VỚI MẮT K&Iacute;NH THỜI TRANG</strong></span></span></span></span></h1>\r\n\r\n<div style=\"padding:0px\">\r\n<table style=\"background:#ffcc99; border-color:initial; border-radius:5px; border-style:initial; margin-bottom:5px; margin-top:5px; padding:0px; width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"background-color:#ffcc99; border-bottom:1px solid #cccccc; border-left:1px solid #cccccc; border-right:1px solid #cccccc; borde