--
-- Cấu trúc bảng cho bảng `products`
--

CREATE TABLE `products` (
  `id` int(10) UNSIGNED NOT NULL,
  `pro_name` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `pro_slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `pro_category_id` int(11) NOT NULL DEFAULT 0,
  `pro_price` int(11) NOT NULL DEFAULT 0,
  `pro_author_id` int(11) NOT NULL DEFAULT 0,
  `pro_sale` tinyint(4) NOT NULL DEFAULT 0,
  `pro_active` tinyint(4) NOT NULL DEFAULT 1,
  `pro_hot` tinyint(4) NOT NULL DEFAULT 0,
  `pro_view` int(11) NOT NULL DEFAULT 0,
  `pro_description` text COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `pro_avatar` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `pro_description_seo` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `pro_keyword_seo` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `pro_title_seo` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `pro_content` text COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `pro_pay` tinyint(4) NOT NULL DEFAULT 0,
  `pro_number` tinyint(4) NOT NULL DEFAULT 0,
  `pro_total_rating` tinyint(4) NOT NULL DEFAULT 0 COMMENT 'Tổng số đánh giá',
  `pro_total_number` tinyint(4) NOT NULL DEFAULT 0 COMMENT 'Tổng số điểm đánh giá'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `products`
--

INSERT INTO `products` (`id`, `pro_name`, `pro_slug`, `pro_category_id`, `pro_price`, `pro_author_id`, `pro_sale`, `pro_active`, `pro_hot`, `pro_view`, `pro_description`, `pro_avatar`, `pro_description_seo`, `pro_keyword_seo`, `created_at`, `updated_at`, `pro_title_seo`, `pro_content`, `pro_pay`, `pro_number`, `pro_total_rating`, `pro_total_number`) VALUES
(6, 'Điện thoại Realme 5 Pro (4GB/128GB)', 'dien-thoai-realme-5-pro-4gb128gb', 2, 5990000, 0, 0, 1, 1, 0, 'Điện thoại Realme 5 Pro (4GB/128GB)', '2019-11-10__realme-5-pro-purple-400x460.png', 'Điện thoại Samsung Galaxy S10+ (512GB)', NULL, '2019-10-29 10:20:09', '2019-11-14 03:56:51', 'Điện thoại Samsung Galaxy S10+ (512GB)', '<h3>Hiệu năng n&acirc;ng cấp mạnh mẽ</h3>\r\n\r\n<p>Cung cấp sức mạnh cho m&aacute;y ch&iacute;nh l&agrave; con chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-qualcomm-snapdragon-712-1178220\" target=\"_blank\" title=\"Tìm hiểu chip Snapdragon 712 8 nhân 64-bit\">Snapdragon 712 8 nh&acirc;n 64-bit</a>&nbsp;&quot;đ&igrave;nh đ&aacute;m&quot; hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-15.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-15.jpg\" title=\"Điện thoại Realme 5 Pro | Thiết kế\" /></a></p>\r\n\r\n<p>Chip di động Snapdragon 712 kh&ocirc;ng chỉ thừa hưởng tất cả tr&iacute; th&ocirc;ng minh AI v&agrave; c&aacute;c t&iacute;nh năng h&agrave;ng đầu của Snapdragon 710 thế hệ trước, m&agrave; c&ograve;n được tăng cường hiệu năng chơi game v&agrave; c&aacute;c trải nghiệm kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-6.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Điểm hiệu năng Antutu Benchmark\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-6.jpg\" title=\"Điện thoại Realme 5 Pro | Điểm hiệu năng Antutu Benchmark\" /></a></p>\r\n\r\n<p>Đ&acirc;y l&agrave; con chip sử dụng kiến tr&uacute;c 10nm ti&ecirc;n tiến, n&ecirc;n hứa hẹn cung cấp hiệu năng tuyệt vời v&agrave; tiết kiệm năng lượng hơn mọi SoC tầm trung kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-7.jpg\" title=\"Điện thoại Realme 5 Pro | Thiết kế\" /></a></p>\r\n\r\n<p>Chiếc smartphone n&agrave;y sở hữu tới 4 GB RAM v&agrave; 128 GB bộ nhớ trong đảm bảo cho bạn c&oacute; thể sử dụng hầu hết c&aacute;c game v&agrave; ứng dụng hiện nay một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<p>C&ocirc;ng nghệ Hyper Boost 2.0 t&iacute;ch hợp tr&ecirc;n m&aacute;y sẽ tự động điều chỉnh hiệu suất chiến game một c&aacute;ch nhanh ch&oacute;ng, điều n&agrave;y sẽ cải thiện tỷ lệ phản hồi khi chơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Công nghệ Hyper Boost 2.0\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-9.jpg\" title=\"Điện thoại Realme 5 Pro | Công nghệ Hyper Boost 2.0\" /></a></p>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-realme\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone Realme chính hãng\">smartphone Realme</a>&nbsp;n&agrave;y được c&agrave;i đặt sẵn ColorOS 6.0.1 dựa tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone Android chính hãng\">Android 9.0 Pie</a>.</p>\r\n\r\n<h3>Camera h&agrave;ng đầu ph&acirc;n kh&uacute;c</h3>\r\n\r\n<p>Realme 5 Pro sở hữu cho m&igrave;nh cụm 4 camera ở mặt lưng với cảm biến ch&iacute;nh c&oacute; độ ph&acirc;n giải l&ecirc;n tới 48 MP.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Bộ 4 camera ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-8.jpg\" title=\"Điện thoại Realme 5 Pro | Bộ 4 camera ấn tượng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; m&aacute;y c&ograve;n sở hữu một cảm biến g&oacute;c si&ecirc;u rộng 8 MP, một cảm biến đo độ s&acirc;u trường ảnh 2 MP v&agrave; một cảm biến d&ugrave;ng để chụp ảnh macro si&ecirc;u gần 2 MP.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-16.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Ảnh chụp bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-16.jpg\" title=\"Điện thoại Realme 5 Pro | Ảnh chụp bằng camera sau\" /></a></p>\r\n\r\n<p>Ứng dụng camera tr&ecirc;n m&aacute;y cũng c&oacute; c&aacute;c t&iacute;nh năng chụp ảnh Ultra 48MP, cảnh đ&ecirc;m, chuy&ecirc;n nghiệp, ch&acirc;n dung, Chroma Boost, HDR, bộ lọc trực tiếp... Tất cả đều được tối ưu h&oacute;a bằng c&ocirc;ng nghệ tr&iacute; tuệ nh&acirc;n tạo AI ti&ecirc;n tiến.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Ảnh chụp góc rộng\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-1.jpg\" title=\"Điện thoại Realme 5 Pro | Ảnh chụp góc rộng\" /></a></p>\r\n\r\n<p>Chưa hết, n&oacute; cũng c&oacute; chế độ quay video si&ecirc;u chậm ở tốc độ 960 fps gi&uacute;p bạn c&oacute; được những đoạn video th&uacute; vị để chia sẻ với bạn b&egrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-17.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Ảnh chụp bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-17.jpg\" title=\"Điện thoại Realme 5 Pro | Ảnh chụp bằng camera sau\" /></a></p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng kh&aacute;c bao gồm chế độ Chroma Boost, gi&uacute;p cải thiện HDR v&agrave; ​​m&agrave;u sắc. M&aacute;y ảnh cũng hỗ trợ quay video 4K với Ổn định h&igrave;nh ảnh điện tử (EIS).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209564/realme-5-pro-tgdd-19.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Realme 5 Pro | Ảnh selfie bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-tgdd-19.jpg\" title=\"Điện thoại Realme 5 Pro | Ảnh selfie bằng camera sau\" /></a></p>\r\n\r\n<p>Camera selfie 16 MP với t&iacute;nh năng l&agrave;m đẹp bằng AI gi&uacute;p bạn c&oacute; thể tự tin đăng l&ecirc;n facebook ngay sau khi chụp.</p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-86px; position:absolute; top:-6px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', 1, 0, 1, 3),
(8, 'Máy tính bảng Samsung Galaxy Tab A 10.5', 'may-tinh-bang-samsung-galaxy-tab-a-105', 1, 8290000, 0, 0, 1, 0, 0, 'Máy tính bảng Samsung Galaxy Tab A 10.5', '2019-11-10__samsung-galaxy-tab-a-105-inch-black-1-400x460.png', 'Máy tính bảng Samsung Galaxy Tab A 10.5\"', NULL, '2019-10-13 19:36:31', '2019-11-27 03:48:39', 'Máy tính bảng Samsung Galaxy Tab A 10.5\"', '<h3>M&agrave;n h&igrave;nh rộng, thoải m&aacute;i lướt web, xem phim</h3>\r\n\r\n<p>Samsung Galaxy Tab A 10.5 inch được trang bị m&agrave;n h&igrave;nh IPS LCD với độ ph&acirc;n giải&nbsp;1.920 x 1.200 pixels, viền bezels được l&agrave;m mỏng cho h&igrave;nh ảnh hiển thị chi tiết dễ d&agrave;ng đọc s&aacute;ch hay xem phim phụ đề m&agrave; kh&ocirc;ng sợ bị mỏi mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Samsung Galaxy Tab A 10.5 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-manhinh.jpg\" title=\"Màn hình Samsung Galaxy Tab A 10.5 inch\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh đủ d&ugrave;ng</h3>\r\n\r\n<p>Được sở hữu bộ vi xử l&yacute; Snapdragon 450 với tốc độ 1.8GHz kết hợp với 3 GB RAM, 32 GB ROM cho hiệu năng kh&aacute; ổn định khi thực hiện c&aacute;c t&aacute;c vụ cơ bản. M&aacute;y hỗ trợ người d&ugrave;ng mở rộng bộ nhớ th&ocirc;ng qua thẻ&nbsp;microSD với dung lượng tối đa 512 GB.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-ch.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Tab A 10.5 inch - Cấu hình đủ dùng | Thegioididong\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-ch.jpg\" title=\"Samsung Galaxy Tab A 10.5 inch - Cấu hình đủ dùng | Thegioididong\" /></a></p>\r\n\r\n<h3>Camera chất lượng tốt</h3>\r\n\r\n<p>Camera trước 5 MP cho chất lượng vừa đủ để gọi video call hoặc&nbsp;selfie. Camera sau 8 MP hỗ trợ đ&egrave;n flash LED gi&uacute;p lưu trữ những khoảnh khắc đẹp của bạn một c&aacute;ch trọn vẹn nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-camera-1.jpg\" onclick=\"return false;\"><img alt=\"Camera trên Samsung Galaxy Tab A 10.5 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-camera-1.jpg\" title=\"Camera trên Samsung Galaxy Tab A 10.5 inch\" /></a></p>\r\n\r\n<h3>Dung lượng pin lớn</h3>\r\n\r\n<p>Galaxy Tab A 10.5 c&ograve;n được cung cấp năng lượng bởi vi&ecirc;n pin dung lượng 7.300 mAh cao hơn nhiều so với c&aacute;c thế hệ trước, gi&uacute;p cho thời gian sử dụng m&aacute;y được l&acirc;u hơn l&ecirc;n đến 13 giờ theo th&ocirc;ng tin được c&ocirc;ng bố từ h&atilde;ng Samsung.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-pin.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Tab A 10.5 inch - Dung lượng pin lớn | Thegioididong\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-pin.jpg\" title=\"Samsung Galaxy Tab A 10.5 inch - Dung lượng pin lớn | Thegioididong\" /></a></p>\r\n\r\n<h3>C&ocirc;ng nghệ &acirc;m thanh&nbsp;Dolby Atmos</h3>\r\n\r\n<p>Với hệ thống &acirc;m thanh gồm 4 loa, giống như tr&ecirc;n Samsung Tab S4. Cụm loa được hỗ trợ c&ocirc;ng nghệ &acirc;m thanh Dolby Atmos, cho chất lượng &acirc;m thanh tuyệt vời khi nghe nhạc, giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-at.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng  Samsung Galaxy Tab A 10.5 inch - Công nghệ âm thanh Dolby Atmos | Thegioididong\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-at.jpg\" title=\"Máy tính bảng  Samsung Galaxy Tab A 10.5 inch - Công nghệ âm thanh Dolby Atmos | Thegioididong\" /></a></p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-108px; position:absolute; top:-6px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', 2, 0, 0, 0),
(9, 'Điện thoại Huawei P30 Pro', 'dien-thoai-huawei-p30-pro', 2, 20690000, 0, 1, 1, 1, 0, 'Điện thoại Huawei P30 Pro', '2019-11-10__huawei-p30-pro-1-400x460.png', 'Điện thoại Huawei P30 Pro', NULL, '2019-10-29 10:19:01', '2020-03-05 07:47:45', 'Điện thoại Huawei P30 Pro', '<h3>Định nghĩa lại camera phone</h3>\r\n\r\n<p>Camera l&agrave; điểm nhấn tr&ecirc;n Huawei P30 Pro với camera ch&iacute;nh&nbsp;SuperSpectrum&nbsp;độ ph&acirc;n giải 40 MP.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro9.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro9.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>Cảm biến&nbsp;SuperSpectrum&nbsp;sử dụng cấu tr&uacute;c RYYB (Red Yellow Yellow Blue), tr&aacute;i ngược với RGB (Red Green Blue), thay thế c&aacute;c pixel m&agrave;u xanh l&aacute; c&acirc;y bằng m&agrave;u v&agrave;ng, theo&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei chính hãng\">Huawei</a>&nbsp;cho ph&eacute;p cảm biến cho &aacute;nh s&aacute;ng nhiều hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro5.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro5.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>Cụm 3 camera c&ograve;n lại bao gồm một ống k&iacute;nh g&oacute;c si&ecirc;u rộng 20 MP, ti&ecirc;u cự 17mm, camera tele 8 MP ti&ecirc;u cự 125mm (zoom 5X, khẩu độ f/3.4 v&agrave; cuối c&ugrave;ng l&agrave; camera ToF.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro2.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro2.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>Cảm biến ToF sẽ thu thập th&ocirc;ng tin chiều s&acirc;u bằng c&aacute;ch chiếu &aacute;nh s&aacute;ng hồng ngoại v&agrave;o đối tượng v&agrave; phản xạ lại camera để đo khoảng c&aacute;ch. C&ocirc;ng nghệ n&agrave;y sẽ cho ph&eacute;p chế độ chụp ch&acirc;n dung c&oacute; hiệu ứng bo-keh chuy&ecirc;n nghiệp hơn gi&uacute;p ph&ocirc;ng nền c&oacute; độ mờ hợp l&yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro6.jpg\" />Ảnh chụp g&oacute;c si&ecirc;u rộng tr&ecirc;n Huawei P30 Pro</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro12.jpg\" />Ảnh chụp zoom quang học 10x&nbsp;tr&ecirc;n Huawei P30 Pro</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro13.jpg\" />Ảnh chụp&nbsp;zoom quang học 50x&nbsp;tr&ecirc;n Huawei P30 Pro</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Huawei đ&atilde; tinh tế khi d&ugrave;ng thấu k&iacute;nh kết hợp gương nằm nghi&ecirc;ng thay v&igrave; xếp chồng như c&aacute;ch truyền thống. Thay đổi n&agrave;y cho ph&eacute;p P30 Pro zoom quang hybrid 10x m&agrave; kh&ocirc;ng l&agrave;m thay đổi chất lượng h&igrave;nh ảnh.</p>\r\n\r\n<p>Chưa dừng lại ở đ&oacute;, m&aacute;y c&ograve;n cho ph&eacute;p người d&ugrave;ng zoom tới 50X gi&uacute;p bạn c&oacute; thể nh&igrave;n r&otilde; những h&igrave;nh ảnh ở xa m&agrave; mắt thường kh&ocirc;ng thể n&agrave;o quan s&aacute;t được.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp ban đêm của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro-tgdd-4.jpg\" title=\"Ảnh chụp ban đêm của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra Huawei P30 Pro c&ograve;n l&agrave; chiếc smartphone c&oacute;&nbsp;độ nhạy s&aacute;ng lớn nhất thế giới l&ecirc;n tới 409.600, kết hợp với khẩu độ rộng hơn sẽ cho ph&eacute;p camera c&oacute; thể thu thập th&ecirc;m nhiều th&ocirc;ng tin &aacute;nh s&aacute;ng để chất lượng h&igrave;nh ảnh được cải thiện.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro7.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro7.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>T&iacute;nh năng Dual-View cho ph&eacute;p người d&ugrave;ng c&ugrave;ng l&uacute;c quay phim bằng hai camera sau với hai ti&ecirc;u cự kh&aacute;c nhau (g&oacute;c cận v&agrave; si&ecirc;u rộng).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Ảnh selfie bằng camera trước trên điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro-tgdd-3.jpg\" title=\"Ảnh selfie bằng camera trước trên điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<h3>Hiệu năng h&agrave;ng đầu thế giới smartphone</h3>\r\n\r\n<p>Cung cấp sức mạnh cho m&aacute;y l&agrave;&nbsp;bộ xử l&yacute;&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/hieu-nang-chip-kirin-980-co-manh-hon-apple-a12-nhu-quang-ba-1125230\" target=\"_blank\" title=\"Tìm hiểu Hisilicon Kirin 980 8 nhân 64-bit\">Hisilicon Kirin 980 8 nh&acirc;n 64-bit</a>&nbsp;kết hợp với 8 GB RAM v&agrave; 256 GB bộ nhớ trong đem lại cho bạn hiệu năng vượt trội.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Điểm hiệu năng Antutu Benchmark của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro-tgdd-1.jpg\" title=\"Điểm hiệu năng Antutu Benchmark của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>Hiện tại, đ&acirc;y l&agrave; con chip mạnh mẽ nhất hiện nay của Huawei cho n&ecirc;n bạn c&oacute; thể tự tin sử dụng tất cả c&aacute;c game v&agrave; ứng dụng nặng nhất hiện nay m&agrave; m&aacute;y vẫn hoạt động một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro1.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro1.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>Huawei P30 Pro chạy tr&ecirc;n phi&ecirc;n bản EMUI 9.1 mới nhất tới từ Huawei với những cải tiến mới như điều hướng cử chỉ hay c&aacute;c t&iacute;nh năng kỹ thuật số.</p>\r\n\r\n<p>M&aacute;y c&ograve;n hỗ trợ v&agrave;i t&iacute;nh năng mới th&uacute; vị như OneHop, cho ph&eacute;p quay lại m&agrave;n h&igrave;nh PC bằng điện thoại hay đồng bộ h&oacute;a tập tin.</p>\r\n\r\n<h3>Những trang bị cao cấp chỉ c&oacute; tr&ecirc;n P30 Pro</h3>\r\n\r\n<p>Tr&ecirc;n&nbsp;Huawei P30 Pro th&igrave; bạn sẽ kh&ocirc;ng c&ograve;n thấy sự xuất hiện của loa thoại v&agrave; Huawei đ&atilde; sử dụng c&ocirc;ng nghệ truyền &acirc;m thanh qua m&agrave;n h&igrave;nh rất độc đ&aacute;o.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; vẫn l&agrave;&nbsp;cảm biến được đặt ngay b&ecirc;n dưới m&agrave;n h&igrave;nh n&ecirc;n bạn sẽ kh&ocirc;ng phải hi sinh bất cứ bộ phận n&agrave;o tr&ecirc;n thiết kế để sử dụng t&iacute;nh năng n&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro9.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro9.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>T&iacute;nh năng nhận diện khu&ocirc;n mặt vẫn xuất hiện v&agrave; bạn c&oacute; thể mở kh&oacute;a m&aacute;y chỉ trong t&iacute;ch tắc.</p>\r\n\r\n<p>Ngo&agrave;i ra m&aacute;y cũng sẽ hỗ trợ chuẩn kh&aacute;ng nước v&agrave; bụi bẩn IP68 cao cấp nhất hiện nay cho bạn thoải m&aacute;i sử dụng trong những điều kiện khắc nghiệt của thời tiết.</p>\r\n\r\n<h3>Dung lượng pin khủng, sạc nhanh si&ecirc;u ấn tượng</h3>\r\n\r\n<p>Huawei P30 Pro trang bị vi&ecirc;n pin 4200 mAh c&ugrave;ng với khả năng sạc si&ecirc;u nhanh SuperCharge 40W.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro11.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro11.jpg\" title=\"Thời lượng pin của điện thoại Huawei P30 Pro chính hãng\" /></a></p>\r\n\r\n<p>Huawei tuy&ecirc;n bố P30 Pro c&oacute; thể sạc từ 0 l&ecirc;n 70% chỉ mất 30 ph&uacute;t v&agrave; đ&acirc;y ch&iacute;nh l&agrave; một trong những chiếc smartphone sạc nhanh nhất tr&ecirc;n thị trường hiện nay.</p>\r\n\r\n<p>C&ocirc;ng nghệ sạc ngược kh&ocirc;ng d&acirc;y cho tai nghe, b&agrave;n chải hoặc điện thoại kh&aacute;c của P30 Pro mang đến sự tiện dụng v&agrave; linh hoạt cho người d&ugrave;ng.</p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-24px; position:absolute; top:-6px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', 3, -1, 1, 5),
(10, 'Máy tính bảng iPad Air 10.5 inch Wifi 64GB 2019', 'may-tinh-bang-ipad-air-105-inch-wifi-64gb-2019', 1, 13990000, 0, 0, 1, 0, 0, 'Máy tính bảng iPad Air 10.5 inch Wifi 64GB 2019', '2019-11-10__ipad-air-105-inch-wifi-2019-gold-400x460.png', 'Máy tính bảng iPad Air 10.5 inch Wifi 64GB 2019', NULL, '2019-10-29 10:19:54', '2019-11-13 01:15:47', 'Máy tính bảng iPad Air 10.5 inch Wifi 64GB 2019', '<h3>Thiết kế chuẩn mực của một chiếc m&aacute;y t&iacute;nh bảng</h3>\r\n\r\n<p>iPad Air 10.5 inch Wifi 2019 kh&ocirc;ng đi theo thiết kế tr&agrave;n viền như tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại chính hãng\">smartphone</a>&nbsp;nhưng điều đ&oacute; lại mang lại cho người d&ugrave;ng cảm gi&aacute;c cầm nắm tốt hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-20193.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-20193.jpg\" title=\"Thiết kế của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>K&iacute;ch thước iPad thường kh&aacute; lớn n&ecirc;n việc sở hữu viền m&agrave;n h&igrave;nh d&agrave;y một ch&uacute;t sẽ gi&uacute;p bạn thoải m&aacute;i cầm ngang hay cầm dọc m&agrave; kh&ocirc;ng sợ chạm v&agrave;o m&agrave;n h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-20195.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-20195.jpg\" title=\"Thiết kế của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Chất liệu kim loại nguy&ecirc;n khối với c&aacute;c g&oacute;c canh bo cong mềm mại gi&uacute;p bạn c&oacute; một thiết bị sang trọng v&agrave; rất thoải m&aacute;i khi cầm sử dụng tr&ecirc;n tay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-20194.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-20194.jpg\" title=\"Thiết kế của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Đ&uacute;ng như t&ecirc;n gọi th&igrave;&nbsp;iPad Air mới vẫn c&oacute; độ mỏng rất ấn tượng n&ecirc;n bạn c&oacute; thể dễ d&agrave;ng cho v&agrave;o balo hay t&uacute;i x&aacute;ch mang đi cafe với bạn b&egrave;.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh sắc n&eacute;t, độ s&aacute;ng cao</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh l&agrave; thứ quan trọng nhất khi người d&ugrave;ng chọn mua một chiếc iPad v&agrave; với k&iacute;ch thước&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang?g=tren-10\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng có kích thước màn hình trên 10 inch\" type=\"Tham khảo giá máy tính bảng có kích thước màn hình trên 10 inch\">10.5 inch</a>&nbsp;th&igrave; bạn cũng sẽ c&oacute; được kh&ocirc;ng gian rất thoải m&aacute;i để giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-20192.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-20192.jpg\" title=\"Màn hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; m&aacute;y c&ograve;n hỗ trợ c&ocirc;ng nghệ True Tone gi&uacute;p bạn lu&ocirc;n cảm thấy dễ chịu d&ugrave; sử dụng trong bất cứ điều kiện m&ocirc;i trường n&agrave;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-20199.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-20199.jpg\" title=\"Màn hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>iPad Air mới được Apple sử dụng m&agrave;n h&igrave;nh cấu tạo bởi nhiều lớp n&ecirc;n h&igrave;nh ảnh sẽ xuất hiện như thể được vẽ trực tiếp tr&ecirc;n một mảnh k&iacute;nh vậy.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-201910.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-201910.jpg\" title=\"Màn hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>M&agrave;u sắc ch&acirc;n thực, g&oacute;c nh&igrave;n cao gi&uacute;p bạn hay con c&aacute;i c&oacute; những gi&acirc;y ph&uacute;t thư gi&atilde;n thoải m&aacute;i nhất.</p>\r\n\r\n<h3>Dung lượng pin vượt trội</h3>\r\n\r\n<p>So với c&aacute;c&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng chính hãng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;Android th&igrave; iPad vượt trội về thời gian sử dụng pin v&agrave; tr&ecirc;n&nbsp;iPad Air 10.5 inch Wifi 2019 Apple tiếp tục duy tr&igrave; lợi thế n&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-201911.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-201911.jpg\" title=\"Thời lượng pin của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Theo c&ocirc;ng bố th&igrave; m&aacute;y c&oacute; thể xem phim hay lướt web li&ecirc;n tục tới 10 giờ gi&uacute;p bạn c&oacute; thể sử dụng thoải m&aacute;i trong 2-3 ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-201912.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-201912.jpg\" title=\"Thời lượng pin của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Một điểm cộng nữa l&agrave; nhớ được tối ưu hiệu quả n&ecirc;n việc để m&aacute;y ở trạng th&aacute;i chờ hay để qua đ&ecirc;m th&igrave; bạn cũng sẽ kh&ocirc;ng bị giảm đi nhiều dung lượng pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-20198.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-20198.jpg\" title=\"Thời lượng pin của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Nhờ việc c&oacute; th&ecirc;m cổng kết nối Smart Conector ở cạnh b&ecirc;n n&ecirc;n iPad Air mới cũng c&oacute; thể kết nối với b&agrave;n ph&iacute;m Smart Keyboard gi&uacute;p bạn l&agrave;m việc hiệu quả hơn.</p>\r\n\r\n<h3>Hiệu năng mạnh mẽ h&agrave;ng đầu hiện nay</h3>\r\n\r\n<p>Về hiệu năng th&igrave; iPad &quot;kh&ocirc;ng c&oacute; đối thủ&quot; khi m&aacute;y sở hữu con chip mạnh mẽ ngang với&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xs-max\" target=\"_blank\" title=\"Tham khảo iPhone Xs Max chính hãng\">iPhone Xs Max</a>&nbsp;&quot;đ&igrave;nh đ&aacute;m&quot; hiện nay, chỉ kh&aacute;c biệt một ch&uacute;t về RAM.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-201913.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-201913.jpg\" title=\"Cấu hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Con chip&nbsp;Apple A12 Bionic với&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang?g=3-gb\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng có RAM 3 GB\" type=\"Tham khảo giá máy tính bảng có RAM 3 GB\">RAM 3 GB</a>&nbsp;gi&uacute;p bạn thoải m&aacute;i sử dụng tất cả game v&agrave; ứng dụng hiện c&oacute; tr&ecirc;n AppStore.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-201914.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-201914.jpg\" title=\"Cấu hình của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>M&aacute;y cũng c&oacute; camera sau 8 MP c&ugrave;ng camera trước 7 MP gi&uacute;p bạn c&oacute; được những bức ảnh vui vẻ hay thực hiện c&aacute;c cuộc gọi video với bạn b&egrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202705/ipad-air-105-inch-wifi-20196.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202705/ipad-air-105-inch-wifi-20196.jpg\" title=\"Camera sau của máy tính bảng iPad Air 10.5 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Nếu sở hữu một chiếc&nbsp;iPad Air 10.5 inch Wifi 2019 th&igrave; bạn ho&agrave;n to&agrave;n c&oacute; thể tự tin sử dụng thoải m&aacute;i trong 3-4 năm nữa m&agrave; m&aacute;y vẫn hoạt động trơn tru v&agrave; mượt m&agrave;.</p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-28px; position:absolute; top:38px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', 1, 1, 0, 0),
(11, 'Máy tính bảng iPad Wifi 32GB (2018)', 'may-tinh-bang-ipad-wifi-32gb-2018', 1, 2000000, 0, 0, 1, 0, 0, 'Máy tính bảng iPad Wifi 32GB (2018)', '2019-11-09__ipad-6th-wifi-32gb-1-400x460.png', 'Máy tính bảng iPad Wifi 32GB (2018)', NULL, '2019-10-29 11:30:20', '2020-03-13 10:59:10', 'Máy tính bảng iPad Wifi 32GB (2018)', '<h3><strong>Thiết kế ho&agrave;n mỹ</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Sản phẩm của Apple\">Apple</a>&nbsp;vẫn giữ ng&ocirc;n ngữ thi&ecirc;́t k&ecirc;́ từ xa xưa tới nay, n&ecirc;n phi&ecirc;n bản iPad 6th cũng kh&ocirc;ng có gì khác lắm với những người ti&ecirc;̀n nhi&ecirc;̣m còn lại.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-6th-wifi-32gb-22.jpg\" onclick=\"return false;\"><img alt=\"Không có nhiều sự khác biết nhưng vẫn rất sang trọng và tinh tế\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-6th-wifi-32gb-22.jpg\" title=\"Không có nhiều sự khác biết nhưng vẫn rất sang trọng và tinh tế\" /></a></p>\r\n\r\n<p><em>Kh&ocirc;ng c&oacute; nhiều sự kh&aacute;c biệt nhưng vẫn rất sang trọng v&agrave; tinh tế</em></p>\r\n\r\n<p>C&aacute;c chi tiết được l&agrave;m rất tỉ mỉ, độ mỏng ấn tượng, mặt lưng được l&agrave;m cong để hạn chế bị cấn tay khi cầm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-wifi-32gb-2018-mau-xam-2-2.jpg\" onclick=\"return false;\"><img alt=\"Không có nhiều sự khác biết nhưng vẫn rất sang trọng và tinh tế\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-wifi-32gb-2018-mau-xam-2-2.jpg\" title=\"Không có nhiều sự khác biết nhưng vẫn rất sang trọng và tinh tế\" /></a></p>\r\n\r\n<p><em>C&aacute;c g&oacute;c cạnh bo cong mềm mại</em></p>\r\n\r\n<p>Ph&iacute;m Home của m&aacute;y vẫn được t&iacute;ch hợp khả năng nhận diện v&acirc;n tay gi&uacute;p bạn mở kh&oacute;a thiết bị nhanh ch&oacute;ng.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh sắc n&eacute;t</strong></h3>\r\n\r\n<p>Với k&iacute;ch thước 9.7 inch kết hợp với tấm nền&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cac-cong-nghe-hien-thi-tren-man-hinh-laptop-597377#ledbacklit\" target=\"_blank\" title=\"Tìm hiểu LED backlit LCD\">LED backlit LCD</a>&nbsp;độ ph&acirc;n giải&nbsp;1536 x 2048 pixels&nbsp;iPad 6th vẫn mang lại cho bạn khả năng hiển thị h&igrave;nh ảnh trung thực c&ugrave;ng g&oacute;c nh&igrave;n rộng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-6th-wifi-32gb-11.jpg\" onclick=\"return false;\"><img alt=\"Màn hình tốt, đáp ứng mọi nhu cầu\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-6th-wifi-32gb-11.jpg\" title=\"Màn hình tốt, đáp ứng mọi nhu cầu\" /></a></p>\r\n\r\n<p><em>M&agrave;n h&igrave;nh tốt, đ&aacute;p ứng mọi nhu cầu</em></p>\r\n\r\n<p>M&agrave;u sắc tr&ecirc;n m&agrave;n h&igrave;nh tươi s&aacute;ng, hiển thị sắc n&eacute;t, th&iacute;ch hợp để đọc b&aacute;o, xem phim cũng như phục vụ những t&iacute;nh năng giải tr&iacute; kh&aacute;c của người d&ugrave;ng.</p>\r\n\r\n<p>Từ đ&acirc;y trải nghiệm l&agrave;m việc hay giải tr&iacute; chơi game tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng&nbsp;iPad Wifi 32GB (2018) sẽ cực kỳ th&iacute;ch.</p>', 1, 1, 11, 45),
(12, 'Điện thoại OPPO F11 Pro 128GB', 'dien-thoai-oppo-f11-pro-128gb', 2, 2000000, 0, 0, 1, 0, 0, 'Điện thoại OPPO F11 Pro 128GB', '2019-11-04__oppo-f11-pro-128gb-400x400.jpg', 'Điện thoại OPPO F11 Pro 128GB', NULL, '2019-11-04 11:20:41', '2019-11-27 03:24:48', 'Điện thoại OPPO F11 Pro 128GB', '<h3>M&agrave;n h&igrave;nh tr&agrave;n viền kh&ocirc;ng khuyết điểm</h3>\r\n\r\n<p>Kh&ocirc;ng &ldquo;tai thỏ&rdquo;, kh&ocirc;ng &ldquo;nốt ruồi&rdquo; gi&uacute;p cho OPPO F11 Pro 128GB kh&aacute;c biệt ho&agrave;n to&agrave;n với nhiều&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>&nbsp;tr&ecirc;n thị trường hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-13.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-13.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Camera selfie nằm ẩn b&ecirc;n trong cơ chế trượt, từ đ&oacute; ch&uacute;ng ta c&oacute; một&nbsp;<a href=\"https://www.thegioididong.com/dtdd?f=man-hinh-tran-vien\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có thiết kế màn hình tràn viền\">m&agrave;n h&igrave;nh tr&agrave;n viền</a>&nbsp;cực kỳ ấn tượng gi&uacute;p người d&ugrave;ng c&oacute; một diện t&iacute;ch trải nghiệm tuyệt vời.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-7.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>OPPO F11 Pro 128GB được trang bị m&agrave;n h&igrave;nh 6.5 inch, độ ph&acirc;n giải Full HD+, tỉ lệ 19.5:9, cộng với viền mỏng, n&oacute; gi&uacute;p chiếc m&aacute;y dễ d&agrave;ng nằm gọn trong l&ograve;ng b&agrave;n tay người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-18.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-18.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Đồng thời, g&oacute;c nh&igrave;n v&agrave; độ s&aacute;ng của m&aacute;y cũng được đ&aacute;nh gi&aacute; l&agrave; kh&aacute; tốt khi người d&ugrave;ng trải nghiệm ngo&agrave;i trời.</p>\r\n\r\n<p>&nbsp;</p>', 1, 1, 1, 4),
(13, 'Máy tính bảng iPad Mini 7.9 inch Wifi Cellular 64GB (2019)', 'may-tinh-bang-ipad-mini-79-inch-wifi-cellular-64gb-2019', 1, 14990000, 0, 0, 1, 0, 0, 'Máy tính bảng iPad Mini 7.9 inch Wifi Cellular 64GB (2019)', '2019-11-09__ipad-mini-79-inch-wifi-cellular-64gb-2019-gold-400x460.png', 'Máy tính bảng iPad Mini 7.9 inch Wifi Cellular 64GB (2019)', NULL, '2019-11-09 06:26:19', '2019-11-13 01:15:12', 'Máy tính bảng iPad Mini 7.9 inch Wifi Cellular 64GB (2019)', '<h2>Đặc điểm nổi bật của iPad Mini 7.9 inch Wifi Cellular 64GB (2019)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/202820/Slider/vi-vn-ipad-mini-79-inch-wifi-cellular-64gb-2019-thietke.jpg\" /></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-chip-apple-a12-bionic-ben-trong-iphone-xs-xs-max-1116982\" target=\"_blank\">T&igrave;m hiểu th&ecirc;m</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/phu-kien-thong-minh/apple-pencil\" target=\"_blank\">T&igrave;m hiểu th&ecirc;m</a></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: C&aacute;p Lightning,Adapter</p>\r\n\r\n<p>&lsaquo;</p>\r\n\r\n<p>&rsaquo;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-mini-79-inch-wifi-cellular-64gb-2019\" target=\"_blank\" title=\"Tham khảo máy tính bảng iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\">iPad Mini 7.9 inch Wifi Cellular 64GB (2019)</a>&nbsp;đ&aacute;nh dấu sự trở lại mạnh mẽ của Apple trong ph&acirc;n kh&uacute;c m&aacute;y t&iacute;nh bảng nhỏ gọn, c&oacute; thể dễ d&agrave;ng mang theo b&ecirc;n m&igrave;nh.</h2>\r\n\r\n<h3>Hiệu năng mạnh mẽ h&agrave;ng đầu</h3>\r\n\r\n<p>iPad Mini 7.9 inch Wifi Cellular 64GB (2019) được Apple trang bị hiệu năng rất ấn tượng với con chip&nbsp;Apple A12 c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang?g=3-gb\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng có RAM 3 GB\" type=\"Tham khảo giá máy tính bảng có RAM 3 GB\">RAM 3 GB</a>&nbsp;v&agrave; 64 GB bộ nhớ trong.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20194.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20194.jpg\" title=\"Cấu hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>Mọi thao t&aacute;c từ cơ bản nhất tới sử dụng c&aacute;c ứng dụng nặng nhất hiện nay th&igrave; chiếc m&aacute;y t&iacute;nh bảng n&agrave;y đều đ&aacute;p ứng một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-2019-tgdd-.jpg\" onclick=\"return false;\"><img alt=\"Điểm hiệu năng Antutu Benchmark của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-2019-tgdd-.jpg\" title=\"Điểm hiệu năng Antutu Benchmark của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>Tất nhi&ecirc;n m&aacute;y cũng sẽ được c&agrave;i đặt sẵn iOS 12 phi&ecirc;n bản mới nhất với c&aacute;c t&iacute;nh năng điều hướng bằng cử chỉ gi&uacute;p bạn sử dụng một c&aacute;ch mượt m&agrave; v&agrave; đơn giản nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20193.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20193.jpg\" title=\"Cấu hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>M&aacute;y t&iacute;nh bảng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo máy tính bảng Apple chính hãng\">iPad</a>&nbsp;Mini 7.9 inch Wifi Cellular 64GB (2019) hỗ trợ nano SIM v&agrave; khả năng kết nối 4G LTE, bạn c&oacute; thể online mọi l&uacute;c m&agrave; kh&ocirc;ng phải lo lắng như c&aacute;c d&ograve;ng m&aacute;y chỉ hỗ trợ wifi kh&aacute; bất tiện.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20198.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20198.jpg\" title=\"Cấu hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>Chiếc&nbsp;iPad Mini mới n&agrave;y cũng sẽ hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/apple-pencil\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng Apple Pencil\" type=\"Tham khảo giá máy tính bảng Apple Pencil\">Apple Pencil</a>&nbsp;mang tới nhiều trải nghiệm th&uacute; vị hơn tr&ecirc;n m&agrave;n h&igrave;nh như viết, vẽ, s&aacute;ng t&aacute;c nghệ thuật...</p>\r\n\r\n<h3>Thiết kế nhỏ gọn dễ d&agrave;ng mang theo</h3>\r\n\r\n<p>Một trong những l&yacute; do ch&iacute;nh m&agrave; Apple hồi sinh d&ograve;ng iPad Mini ch&iacute;nh l&agrave; bởi thiết kế nhỏ gọn của n&oacute; gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng mang theo đi bất cứ đ&acirc;u.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20192.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20192.jpg\" title=\"Thiết kế của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>Bạn sẽ c&oacute; cho m&igrave;nh một chiếc iPad với k&iacute;ch thước m&agrave;n h&igrave;nh 7.9 inch kh&ocirc;ng qu&aacute; lớn v&agrave; thậm ch&iacute; bạn c&oacute; thể cho chiếc iPad n&agrave;y v&agrave;o t&uacute;i quần của m&igrave;nh. Độ ph&acirc;n giải của m&aacute;y l&agrave; 2224 x 1668 pixels mang lại trải nghiệm l&agrave;m việc giải tr&iacute; xem phim tuyệt vời, đặc biệt l&agrave; khi thư giản c&ugrave;ng với gia đ&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20195.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20195.jpg\" title=\"Thiết kế của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>Thiết kế kim loại nguy&ecirc;n khối với c&aacute;c đường cắt kim cương sang trọng vẫn l&agrave; một trong những thiết kế đẹp nhất d&agrave;nh cho những chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng chính hãng\">m&aacute;y t&iacute;nh bảng</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20196.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20196.jpg\" title=\"Thiết kế của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>Trọng lượng của m&aacute;y cũng kh&aacute; nhẹ gi&uacute;p bạn c&oacute; thể sử dụng l&acirc;u m&agrave; vẫn kh&ocirc;ng cảm thấy mỏi tay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20197.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20197.jpg\" title=\"Màn hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>Một n&acirc;ng cấp kh&aacute; đ&aacute;ng gi&aacute; so với thế hệ cũ ch&iacute;nh l&agrave; việc hỗ trợ c&ocirc;ng nghệ hiển thị True Tone mang lại khả năng điều chỉnh m&agrave;n h&igrave;nh với c&aacute;c t&igrave;nh huống &aacute;nh s&aacute;ng kh&aacute;c nhau.</p>\r\n\r\n<p>Ngo&agrave;i ra th&igrave; ph&iacute;m Home vật l&yacute; quen thuộc cũng xuất hiện tr&ecirc;n m&aacute;y c&ugrave;ng c&ocirc;ng nghệ cảm biến v&acirc;n tay thế hệ thứ 2 gi&uacute;p bạn bảo mật v&agrave; mở kh&oacute;a m&aacute;y nhanh ch&oacute;ng.</p>\r\n\r\n<h3>Thời lượng pin thoải m&aacute;i d&ugrave;ng 2 ng&agrave;y</h3>\r\n\r\n<p>Với&nbsp;iPad Mini 7.9 inch Wifi 2019 th&igrave; bạn sẽ c&oacute; được 10 tiếng sử dụng li&ecirc;n tục với c&aacute;c t&aacute;c vụ như xem video hay lướt web, một con số m&agrave; nhiều người hằng mơ ước.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20191.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202820/ipad-mini-79-inch-wifi-cellular-64gb-20191.jpg\" title=\"Cấu hình của điện thoại iPad Mini 7.9 inch Wifi Cellular 64GB (2019) chính hãng\" /></a></p>\r\n\r\n<p>Nhờ việc tối ưu h&oacute;a hiệu quả n&ecirc;n việc để thiết bị qua đ&ecirc;m cũng sẽ kh&ocirc;ng khiến bạn tốn qu&aacute; nhiều dung lượng pin.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; m&aacute;y vẫn giữ lại cho ch&uacute;ng ta jack cắm tai nghe 3.5mm quen thuộc gi&uacute;p bạn dễ d&agrave;ng vừa c&oacute; thể nghe nhạc v&agrave; vừa c&oacute; thể sạc pin.</p>', 1, 1, 0, 0);
INSERT INTO `products` (`id`, `pro_name`, `pro_slug`, `pro_category_id`, `pro_price`, `pro_author_id`, `pro_sale`, `pro_active`, `pro_hot`, `pro_view`, `pro_description`, `pro_avatar`, `pro_description_seo`, `pro_keyword_seo`, `created_at`, `updated_at`, `pro_title_seo`, `pro_content`, `pro_pay`, `pro_number`, `pro_total_rating`, `pro_total_number`) VALUES
(15, 'Máy tính bảng Samsung Galaxy Tab S6', 'may-tinh-bang-samsung-galaxy-tab-s6', 1, 18490000, 0, 0, 1, 0, 0, 'Máy tính bảng Samsung Galaxy Tab S6', '2019-11-10__samsung-galaxy-tab-s6-400x460.png', 'Máy tính bảng Samsung Galaxy Tab S6', NULL, '2019-11-10 06:24:47', '2019-11-13 08:19:40', 'Máy tính bảng Samsung Galaxy Tab S6', '<h3>Ngoại h&igrave;nh mỏng nhẹ, đẹp mắt</h3>\r\n\r\n<p>Ấn tượng đầu ti&ecirc;n về chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" target=\"_blank\" title=\"Tham khảo máy tính bảng Samsung chính hãng\">m&aacute;y t&iacute;nh bảng Samsung</a>&nbsp;n&agrave;y ch&iacute;nh l&agrave; n&oacute; rất mỏng v&agrave; sang trọng, nếu&nbsp;bạn đang c&oacute; suy nghĩ l&agrave; những chiếc m&aacute;y t&iacute;nh bảng thường c&oacute; k&iacute;ch thước lớn, trọng lượng nặng v&agrave; kh&oacute; mang theo th&igrave; đ&acirc;y sẽ l&agrave; một trải nghiệm ho&agrave;n to&agrave;n kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd1.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd1.jpg\" title=\"Điện thoại Samsung Galaxy Tab S6 | Thiết kế\" /></a></p>\r\n\r\n<p>Với độ d&agrave;y chỉ 5.7 mm th&igrave; bạn c&oacute; thể dễ d&agrave;ng cho v&agrave;o t&uacute;i x&aacute;ch v&agrave; mang theo đi bất cứ đ&acirc;u m&agrave; m&igrave;nh muốn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd3.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd3.jpg\" title=\"Điện thoại Samsung Galaxy Tab S6 | Thiết kế\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang?g=tren-10\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng chính hãng màn hình khoảng 10 inch\" type=\"Tham khảo giá máy tính bảng chính hãng màn hình khoảng 10 inch\">M&agrave;n h&igrave;nh k&iacute;ch thước lớn</a>&nbsp;10.5 inch với tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-la-gi-905770\" target=\"_blank\" title=\"Tìm hiểu tấm nền Super AMOLED\">Super AMOLED</a>&nbsp;chất lượng cao gi&uacute;p bạn dễ d&agrave;ng sử dụng ngay cả trong điều kiện &aacute;nh s&aacute;ng mạnh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd2.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd2.jpg\" title=\"Điện thoại Samsung Galaxy Tab S6 | Màn hình\" /></a></p>\r\n\r\n<p>Samsung Galaxy Tab S6&nbsp;ho&agrave;n to&agrave;n hỗ trợ tốt mọi người d&ugrave;ng cho mọi nhu cầu sử dụng: từ giải tr&iacute; xem phim, chơi game, cho đến học tập, l&agrave;m việc khi phải sử dụng m&aacute;y l&acirc;u d&agrave;i.</p>\r\n\r\n<h3>Chiếc tablet đầu ti&ecirc;n c&oacute; camera k&eacute;p</h3>\r\n\r\n<p>Samsung Galaxy Tab S6 l&agrave; chiếc m&aacute;y t&iacute;nh bảng đầu ti&ecirc;n của Samsung được trang bị camera k&eacute;p &quot;xịn x&ograve;&quot;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd6.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd6.jpg\" title=\"Điện thoại Samsung Galaxy Tab S6 | Camera sau\" /></a></p>\r\n\r\n<p>Samsung n&oacute;i rằng 86% những người đ&atilde; mua Galaxy Tab S4 đ&atilde; sử dụng ứng dụng m&aacute;y ảnh &quot;thường xuy&ecirc;n&quot; v&igrave; thế h&atilde;ng muốn n&acirc;ng cao trải nghiệm về nhiếp ảnh tr&ecirc;n chiếc tablet mới của m&igrave;nh.</p>\r\n\r\n<p>Ngo&agrave;i cảm biến ch&iacute;nh 13 MP th&igrave; m&aacute;y sở hữu th&ecirc;m 1 cảm biến g&oacute;c si&ecirc;u rộng với độ ph&acirc;n giải 5 MP nữa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd11.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd11.jpg\" title=\"Điện thoại Samsung Galaxy Tab S6 | Camera sau\" /></a></p>\r\n\r\n<p>Giờ đ&acirc;y bạn c&oacute; thể s&aacute;ng tạo cho m&igrave;nh nhiều bức ảnh đẹp hơn để chia sẻ với bạn b&egrave; nhờ camera g&oacute;c si&ecirc;u rộng n&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Ảnh chụp bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd-4.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S6 | Ảnh chụp bằng camera sau\" /></a></p>\r\n\r\n<p>Bạn vẫn sẽ c&oacute; camera trước 8 MP để thực hiện c&aacute;c cuộc gọi video với bạn b&egrave; v&agrave; người th&acirc;n.</p>\r\n\r\n<h3>B&uacute;t S Pen nhiều t&iacute;nh năng</h3>\r\n\r\n<p>Samsung Galaxy Tab S6 sở hữu b&uacute;t S Pen mới với khả năng hỗ trợ Bluetooth như tr&ecirc;n chiếc Galaxy Note 9.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd9.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Bút S-Pen\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd9.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S6 | Bút S-Pen\" /></a></p>\r\n\r\n<p>Kết hợp b&uacute;t với m&aacute;y t&iacute;nh bảng sẽ hỗ trợ người d&ugrave;ng c&oacute; thể note, ph&aacute;c thảo c&aacute;c bản vẽ đồ họa, hoặc sử dụng như b&agrave;n di chuột thực thụ.</p>\r\n\r\n<p>S Pen giờ đ&acirc;y c&oacute; thể thực hiện nhiều thao t&aacute;c hơn so với phi&ecirc;n bản cũ v&agrave; tr&ecirc;n ph&acirc;n th&acirc;n của chiếc tablet n&agrave;y c&ograve;n c&oacute; từ t&iacute;nh để bạn c&oacute; thể gắn S Pen v&agrave;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd12.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Bàn phím\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd12.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S6 | Bàn phím\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;&nbsp;Galaxy Tab S6 c&ograve;n hỗ trợ th&ecirc;m b&agrave;n ph&iacute;m rời gi&uacute;p biến chiếc m&aacute;y th&agrave;nh một chiếc laptop &quot;thực thụ&quot;.</p>\r\n\r\n<p>Điểm n&acirc;ng cấp của bộ b&agrave;n ph&iacute;m n&agrave;y so với thế hệ cũ ch&iacute;nh l&agrave; hỗ trợ th&ecirc;m phần&nbsp;trackpad để bạn c&oacute; thể sử dụng tiện lợi hơn thay v&igrave; phải cảm ứng tr&ecirc;n phần m&agrave;n h&igrave;nh như trước.</p>\r\n\r\n<h3>Hiệu năng h&agrave;ng đầu thế giới Android</h3>\r\n\r\n<p>Samsung Galaxy Tab S6&nbsp;với nền tảng Android 9.0, trang bị chip&nbsp;Qualcomm Snapdragon 855 8 nh&acirc;n, RAM 6 GB c&ugrave;ng bộ nhớ ROM 128 GB cho hoạt động nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd8.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Cấu hình\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd8.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S6 | Cấu hình\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, vi&ecirc;n pin 7.040 mAh cho thời gian ph&aacute;t video l&ecirc;n đến 15 giờ li&ecirc;n tục theo như lời tuy&ecirc;n bố từ h&atilde;ng Samsung.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd-2.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Điểm Antutu Benmark\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd-2.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S6 | Điểm Antutu Benmark\" /></a></p>\r\n\r\n<p>M&aacute;y cũng hỗ trợ sạc nhanh qua cổng USB Type C gi&uacute;p bạn r&uacute;t ngắn được đ&aacute;ng kể thời gian chờ sạc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/208870/samsung-galaxy-tab-s6-tgdd10.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S6 | Cảm biến vân tay\" src=\"https://cdn.tgdd.vn/Products/Images/522/208870/samsung-galaxy-tab-s6-tgdd10.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S6 | Cảm biến vân tay\" /></a></p>\r\n\r\n<p>Cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh cũng xuất hiện tr&ecirc;n chiếc tablet n&agrave;y gi&uacute;p bạn c&oacute; thể trải nghiệm việc mở kh&oacute;a bằng v&acirc;n tay ở mặt trước m&agrave; kh&ocirc;ng phải hi sinh bất cứ bộ phận n&agrave;o.</p>', 1, 1, 0, 0),
(16, 'Máy tính bảng Huawei MediaPad T3', 'may-tinh-bang-huawei-mediapad-t3', 1, 4490000, 0, 0, 1, 0, 0, 'Máy tính bảng Huawei MediaPad T3', '2019-11-10__huawei-mediapad-t3-10-1-33397-chitiet-400x460.png', 'Máy tính bảng Huawei MediaPad T3', NULL, '2019-11-10 06:26:57', '2019-11-13 01:15:24', 'Máy tính bảng Huawei MediaPad T3', '<h3><strong>Thiết kế mỏng, đẹp</strong></h3>\r\n\r\n<p>Về tổng thể, m&aacute;y nổi bật với phần khung viền kim loại sang trọng, kết hợp m&agrave;u sắc hiện đại cho cảm gi&aacute;c kh&aacute; thanh lịch, nhẹ nh&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-103.png\" onclick=\"return false;\"><img alt=\"Thiết kế đơn giản nhưng vẫn thu hút được người dùng\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-103.png\" title=\"Thiết kế đơn giản nhưng vẫn thu hút được người dùng\" /></a></p>\r\n\r\n<p><em>Thiết kế đơn giản nhưng vẫn thu h&uacute;t được người d&ugrave;ng</em></p>\r\n\r\n<p>Ngo&agrave;i ra, với trọng lượng kh&aacute; nhẹ c&ugrave;ng với 4 g&oacute;c bo tr&ograve;n gi&uacute;p m&aacute;y c&oacute; trải nghiệm cầm nắm kh&aacute; thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-105.png\" onclick=\"return false;\"><img alt=\"Các phím bấm được hoàn thiện tốt, độ đàn hồi cao\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-105.png\" title=\"Các phím bấm được hoàn thiện tốt, độ đàn hồi cao\" /></a></p>\r\n\r\n<p><em>C&aacute;c ph&iacute;m bấm được ho&agrave;n thiện tốt, độ đ&agrave;n hồi cao</em></p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh hiển thị tốt</strong></h3>\r\n\r\n<p dir=\"ltr\">Sở hữu m&agrave;n h&igrave;nh k&iacute;ch thước 10 inch kh&ocirc;ng qu&aacute; lớn, c&oacute; thể nh&eacute;t vừa một chiếc balo dạng nhỏ, Huawei MediaPad T3 10 (2017) Spen sử dụng tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-man-hinh-pls-lcd-la-gi-901358\" target=\"_blank\" title=\"Tìm hiểu màn hình PLS LCD\">PLS LCD</a>&nbsp;cho khả năng hiển thị tốt.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-107.png\" onclick=\"return false;\"><img alt=\"Màn hình lớn cho trải nghiệm, sử dụng thoải mái\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-107.png\" title=\"Màn hình lớn cho trải nghiệm, sử dụng thoải mái\" /></a></p>\r\n\r\n<p dir=\"ltr\"><em>M&agrave;n h&igrave;nh lớn cho trải nghiệm, sử dụng thoải m&aacute;i</em></p>\r\n\r\n<p dir=\"ltr\">Độ ph&acirc;n giải 1280 x 800 pixels cho chất lượng h&igrave;nh ảnh sắc n&eacute;t, sử dụng tốt khi đi ngo&agrave;i trời.</p>\r\n\r\n<h3><strong>Cấu h&igrave;nh ổn định</strong></h3>\r\n\r\n<p>Cung cấp sức mạnh cho m&aacute;y l&agrave; con chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-qualcomm-snapdragon-425-1009665\" target=\"_blank\" title=\"Tìm hiểu chip Qualcomm MSM8917\">Qualcomm MSM8917</a>&nbsp;và RAM 2 GB&nbsp;ho&agrave;n to&agrave;n đ&aacute;p ứng tốt c&aacute;c nhu cầu thường ng&agrave;y của bạn, từ đọc b&aacute;o online, lướt facebook hay xem phim đến chơi game.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-108.png\" onclick=\"return false;\"><img alt=\"Hiệu năng không quá ấn tượng nhưng với tác vụ cơ bản máy vẫn hoạt động ổn định\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-108.png\" title=\"Hiệu năng không quá ấn tượng nhưng với tác vụ cơ bản máy vẫn hoạt động ổn định\" /></a></p>\r\n\r\n<p><em>Hiệu năng kh&ocirc;ng qu&aacute; ấn tượng nhưng với t&aacute;c vụ cơ bản m&aacute;y vẫn hoạt động ổn định</em></p>\r\n\r\n<p>Khả năng chơi game nặng của m&aacute;y sẽ c&oacute; phần hạn chế, d&ugrave; vậy so với tầm gi&aacute; th&igrave; trải nghiệm tr&ecirc;n MediaPad T3 10 (2017) vẫn kh&aacute; ấn tượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-106.png\" onclick=\"return false;\"><img alt=\"Huawei cũng không quên trang bị cho máy nhiều tiện ích đi kèm giúp bạn khai thác tối đa màn hình lớn của máy\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-106.png\" title=\"Huawei cũng không quên trang bị cho máy nhiều tiện ích đi kèm giúp bạn khai thác tối đa màn hình lớn của máy\" /></a></p>\r\n\r\n<p><em>Huawei cũng kh&ocirc;ng qu&ecirc;n trang bị cho m&aacute;y nhiều tiện &iacute;ch đi k&egrave;m gi&uacute;p bạn khai th&aacute;c tối đa m&agrave;n h&igrave;nh lớn của m&aacute;y</em></p>\r\n\r\n<p>MediaPad T3 10 (2017) hoạt đ&ocirc;̣ng tr&ecirc;n n&ecirc;̀n tảng Android 7 mới mẻ n&ecirc;n sở hữu cho m&igrave;nh khả năng chia đ&ocirc;i màn hình đ&ecirc;̉ sử dụng hai ứng dụng cùng m&ocirc;̣t lúc. K&ecirc;́t hợp với màn hình k&iacute;ch thước lớn thì đ&acirc;y quả là m&ocirc;̣t lợi th&ecirc;́ r&acirc;́t lớn cho những người thích làm vi&ecirc;̣c đa nhi&ecirc;̣m.</p>\r\n\r\n<h3><strong>Camera đủ d&ugrave;ng</strong></h3>\r\n\r\n<p>Việc chụp ảnh bằng một chiếc m&aacute;y t&iacute;nh bảng chưa bao giờ được người d&ugrave;ng đ&aacute;nh gi&aacute; cao v&agrave; Huawei cũng chỉ trang bị cho MediaPad T3 10 (2017) bộ đ&ocirc;i camera đủ đ&aacute;p ứng c&aacute;c nhu cầu cơ bản.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-1040.jpg\" onclick=\"return false;\"><img alt=\"Bộ đôi camera giúp bạn lưu lại những khoảnh khắc đáng nhớ\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-1040.jpg\" title=\"Bộ đôi camera giúp bạn lưu lại những khoảnh khắc đáng nhớ\" /></a></p>\r\n\r\n<p><em>Bộ đ&ocirc;i camera gi&uacute;p bạn lưu lại những khoảnh khắc đ&aacute;ng nhớ</em></p>\r\n\r\n<p>M&aacute;y sở hữu camera sau độ ph&acirc;n giải 5 MP, ảnh chụp ở điều kiện ban ng&agrave;y ở mức chấp nhận được mặc d&ugrave; m&agrave;u sắc c&oacute; phần hơi nhợt nhạt so với thực tế.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-102.png\" onclick=\"return false;\"><img alt=\"Camera trước giúp bạn có thể thực hiện các cuộc gọi video với bạn bè\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-102.png\" title=\"Camera trước giúp bạn có thể thực hiện các cuộc gọi video với bạn bè\" /></a></p>\r\n\r\n<p><em>Camera trước gi&uacute;p bạn c&oacute; thể thực hiện c&aacute;c cuộc gọi video với bạn b&egrave;</em></p>\r\n\r\n<p>Còn camera trước cũng có đ&ocirc;̣ ph&acirc;n giải chỉ dừng lại ở mức 2 MP nhưng cũng đủ đ&ecirc;̉ chúng ta thực hi&ecirc;̣n các cu&ocirc;̣c gọi video.</p>\r\n\r\n<h3><strong>Hỗ trợ kết nối 3G/4G</strong></h3>\r\n\r\n<p>M&aacute;y cũng hỗ trợ khe cắm&nbsp;<a href=\"https://www.thegioididong.com/the-nho-dien-thoai\" title=\"thẻ nhớ\" type=\"thẻ nhớ\">thẻ nhớ</a>&nbsp;mở rộng tối đa l&ecirc;n tới 32 GB c&ugrave;ng 1 khe cắm&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-cac-loai-sim-thong-dung-sim-thuong-micro--590216#nanosim\" title=\"nano sim\" type=\"nano sim\">Nano Sim</a>&nbsp;v&agrave; kết nối 4G tốc độ cao gi&uacute;p bạn truy cập internet với tốc độ nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-1010.jpg\" onclick=\"return false;\"><img alt=\"Khe cắm SIM tiện lợi đảm bảo bạn luôn online mọi lúc mọi nơi\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-1010.jpg\" title=\"Khe cắm SIM tiện lợi đảm bảo bạn luôn online mọi lúc mọi nơi\" /></a></p>\r\n\r\n<p><em>Khe cắm SIM tiện lợi đảm bảo bạn lu&ocirc;n online mọi l&uacute;c mọi nơi</em></p>', 1, 1, 0, 0),
(18, 'Máy tính bảng Mobell Tab 8 Pro', 'may-tinh-bang-mobell-tab-8-pro', 1, 2450000, 0, 0, 1, 0, 0, 'Máy tính bảng Mobell Tab 8 Pro', '2019-11-10__mobell-tab-8-pro-2-400x460.png', 'Máy tính bảng Mobell Tab 8 Pro', NULL, '2019-11-10 06:30:31', '2019-11-13 08:19:37', 'Máy tính bảng Mobell Tab 8 Pro', '<h3 dir=\"ltr\"><strong>Hiệu năng ổn định trong tầm gi&aacute;</strong></h3>\r\n\r\n<p dir=\"ltr\">Mobell Tab 8 Pro sở hữu con chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-vi-xu-ly-tren-di-dong-chip-mediatek-592855\" title=\"MTK 8321 4 nhân\" type=\"MTK 8321 4 nhân\">MTK 8321 4 nh&acirc;n</a>&nbsp;1.33 GHz, chip đồ họa Mali-400MP, 1 GB RAM c&ugrave;ng bộ nhớ trong được n&acirc;ng l&ecirc;n 16 GB (<a href=\"https://www.thegioididong.com/may-tinh-bang/mobell-tab-8\" title=\"Mobell Tab 8\" type=\"Mobell Tab 8\">Mobell Tab 8</a>&nbsp;l&agrave; 8 GB).</p>\r\n\r\n<p dir=\"ltr\">Mobell cũng trang bị cho Tab 8 Pro một loạt c&aacute;c cảm biến như G-cảm biến, &aacute;nh s&aacute;ng gi&uacute;p bạn sử dụng chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" title=\"Tablet\" type=\"Tablet\">tablet</a>&nbsp;của m&igrave;nh hiệu quả hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/76022/mobell-tab-8-pro-5-2.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng ổn định\" src=\"https://cdn.tgdd.vn/Products/Images/522/76022/mobell-tab-8-pro-5-2.jpg\" title=\"Hiệu năng ổn định\" /></a></p>\r\n\r\n<p dir=\"ltr\">Thử nghiệm với tựa game Plant &amp; Zombie cho thấy m&aacute;y c&oacute; hiệu năng tốt, mượt m&agrave; kh&ocirc;ng giật, lag. M&aacute;y được trang bị 2 sim, gi&uacute;p người d&ugrave;ng thỏa sức thư gi&atilde;n hay l&agrave;m việc với sự trợ gi&uacute;p của những nh&agrave; mạng kh&aacute;c nhau với kết nối 3G tốc độ cao.</p>\r\n\r\n<p dir=\"ltr\">Việc giữ li&ecirc;n lạc v&agrave; kết nối &nbsp;cho c&ocirc;ng việc giờ đ&acirc;y đ&atilde; kh&ocirc;ng c&ograve;n kh&oacute; khăn với Mobell Tab 8 Pro!</p>\r\n\r\n<h3 dir=\"ltr\"><strong>Pin khủng, x&agrave;i l&acirc;u đến 1 ng&agrave;y!</strong></h3>\r\n\r\n<p dir=\"ltr\">Mobell Tab 8 Pro được trang bị&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/pin-smartphone-li-ion-va-phuong-thuc-su-dung-pin-595433\" title=\"pin Lithium - Lion\" type=\"pin Lithium - Lion\">pin Lithium - Lion</a>&nbsp;với dung lượng l&ecirc;n đến 5200 mAh c&ugrave;ng chế độ tiết kiệm pin đặc biệt, m&aacute;y c&oacute; thể hoạt động tốt l&ecirc;n đến 1 ng&agrave;y m&agrave; kh&ocirc;ng cần th&ecirc;m bất k&igrave; c&ocirc;ng cụ sạc pin hay hỗ trợ n&agrave;o!</p>\r\n\r\n<h3 dir=\"ltr\"><strong>Thiết kế sang trọng</strong></h3>\r\n\r\n<p dir=\"ltr\">Mặt trước chiếm phần lớn diện t&iacute;ch của Mobell Tab 8 Pro ch&iacute;nh l&agrave; m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước 8 inch IPS LCD.&nbsp;C&aacute;c g&oacute;c cạnh của m&aacute;y được bo tr&ograve;n mềm mại đem lại cảm gi&aacute;c cầm nắm kh&aacute; tốt khi sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/76022/mobell-tab-8-pro-2-2.jpg\" onclick=\"return false;\"><img alt=\"Máy sở hữu độ mỏng ấn tượng chỉ 7.9 mm\" src=\"https://cdn.tgdd.vn/Products/Images/522/76022/mobell-tab-8-pro-2-2.jpg\" title=\"Máy sở hữu độ mỏng ấn tượng chỉ 7.9 mm\" /></a></p>\r\n\r\n<p><em>M&aacute;y sở hữu độ mỏng ấn tượng chỉ 7.9 mm</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/76022/mobell-tab-8-pro-1-2.jpg\" onclick=\"return false;\"><img alt=\"Khu vực đặt khe cắm sim và thẻ nhớ\" src=\"https://cdn.tgdd.vn/Products/Images/522/76022/mobell-tab-8-pro-1-2.jpg\" title=\"Khu vực đặt khe cắm sim và thẻ nhớ\" /></a></p>\r\n\r\n<p><em>Khu vực đặt khe cắm sim v&agrave;&nbsp;thẻ nhớ</em></p>\r\n\r\n<h3 dir=\"ltr\"><strong>M&agrave;n h&igrave;nh rộng, sắc n&eacute;t</strong></h3>\r\n\r\n<p dir=\"ltr\">Mobell Tab 8 Pro c&oacute; m&agrave;n h&igrave;nh k&iacute;ch thước 8 inch độ ph&acirc;n giải 1280 x 800 pixels c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" title=\"công nghệ IPS LCD\" type=\"công nghệ IPS LCD\">c&ocirc;ng nghệ IPS LCD</a>&nbsp;đem lại khả năng hiển thị ở mức kh&aacute;.</p>\r\n\r\n<p dir=\"ltr\">M&aacute;y hiển thị r&otilde; n&eacute;t, m&agrave;u sắc tốt với g&oacute;c nh&igrave;n rộng, mang lại trải nghiệm tốt trong nhiều điều kiện &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/76022/mobell-tab-8-pro-3-2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình 8 inch hiển thị tốt\" src=\"https://cdn.tgdd.vn/Products/Images/522/76022/mobell-tab-8-pro-3-2.jpg\" title=\"Màn hình 8 inch hiển thị tốt\" /></a></p>\r\n\r\n<p><em>M&agrave;n h&igrave;nh 8 inch hiển thị tốt</em></p>\r\n\r\n<p dir=\"ltr\">Hiển thị với c&aacute;c game c&oacute; đồ họa cao, m&aacute;y c&oacute; hiện tượng giật lag nhẹ. Tuy nhi&ecirc;n, với nhu cầu giải tr&iacute; cơ bản, m&aacute;y c&oacute; thể chạy ổn định, tốt, chuyển khung h&igrave;nh kh&ocirc;ng giật lag.</p>\r\n\r\n<h3 dir=\"ltr\"><strong>Camera chất lượng 8 MP</strong></h3>\r\n\r\n<p dir=\"ltr\">Mobell Tab 8 Pro được trang bị camera ch&iacute;nh 8 MP hỗ trợ đ&egrave;n flash trợ s&aacute;ng c&ugrave;ng camera selfie 5 MP được n&acirc;ng cấp cho những bức ảnh selfie c&oacute; chất lượng tốt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/76022/mobell-tab-8-pro-4-2.jpg\" onclick=\"return false;\"><img alt=\"Được nâng lên từ 2 MP của Tab 8 lên đến 5 MP là một trong những cải thiện lớn của máy\" src=\"https://cdn.tgdd.vn/Products/Images/522/76022/mobell-tab-8-pro-4-2.jpg\" title=\"Được nâng lên từ 2 MP của Tab 8 lên đến 5 MP là một trong những cải thiện lớn của máy\" /></a></p>\r\n\r\n<p><em>Được n&acirc;ng l&ecirc;n từ 2 MP của Tab 8 l&ecirc;n đến 5 MP l&agrave; một trong những cải thiện lớn của m&aacute;y</em></p>\r\n\r\n<p dir=\"ltr\">H&igrave;nh ảnh chụp từ m&aacute;y cho phản &aacute;nh tốt, r&otilde; n&eacute;t v&agrave; đẹp trong nhiều điều kiện chụp kh&aacute;c nhau với c&aacute;c chức năng chạm lấy n&eacute;t, nhận diện khu&ocirc;n mặt... những yếu tố chỉ c&oacute; trong c&aacute;c phi&ecirc;n bản cao cấp.</p>', 1, 1, 0, 0),
(19, 'Máy tính bảng iPad 10.2 inch', 'may-tinh-bang-ipad-102-inch', 1, 13990000, 0, 0, 1, 1, 0, 'Máy tính bảng iPad 10.2 inch', '2019-11-10__ipad-10-2-inch-wifi-cellular-32gb-2019-gold-400x460.png', 'Máy tính bảng iPad 10.2 inch', NULL, '2019-11-10 06:32:42', '2020-03-13 03:45:23', 'Máy tính bảng iPad 10.2 inch', '<h3>M&agrave;n h&igrave;nh lớn, x&agrave;i đ&atilde; hơn</h3>\r\n\r\n<p>Chắc chắn những người lựa chọn&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo iPad chính hãng\">iPad</a>&nbsp;phần lớn đều th&iacute;ch m&agrave;n h&igrave;nh k&iacute;ch thước lớn của n&oacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd13.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Màn hình giải trí sắc nét\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd13.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Màn hình giải trí sắc nét\" /></a></p>\r\n\r\n<p>So với thế hệ tiền nhiệm th&igrave; iPad năm nay được Apple n&acirc;ng cấp k&iacute;ch thước m&agrave;n h&igrave;nh l&ecirc;n một ch&uacute;t l&agrave; 10.2 inch so với 9.7 inch như thế hệ trước.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd9.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Màn hình kích thước lớn\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd9.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Màn hình kích thước lớn\" /></a></p>\r\n\r\n<p>Điều n&agrave;y gi&uacute;p bạn sẽ c&oacute; nhiều kh&ocirc;ng gian hơn để sử dụng cho việc học tập, l&agrave;m việc v&agrave; giải tr&iacute; của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd7.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Trải nghiệm giải trí\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd7.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Trải nghiệm giải trí\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; th&igrave; tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ledbacklit-ips-lcd-la-gi-905757\" target=\"_blank\" title=\"Tìm hiểu màn hình LED backlit LCD\">LED backlit LCD</a>&nbsp;được đ&aacute;ng gi&aacute; rất cao bởi độ s&aacute;ng v&agrave; m&agrave;u sắc trung thực m&agrave; n&oacute; mang lại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd8-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Thiết kế cổng kết nối smart connector tiện lợi\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd8-1.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Thiết kế cổng kết nối smart connector tiện lợi\" /></a></p>\r\n\r\n<p>M&aacute;y vẫn giữ kiểu thiết kế truyền thống vốn đ&atilde; tồn tại tr&ecirc;n nhiều thế hệ iPad của Apple nhưng vẫn kh&ocirc;ng k&eacute;m phần sang trọng v&agrave; tiện dụng.</p>\r\n\r\n<h3>Thời lượng pin vượt trội</h3>\r\n\r\n<p>Những chiếc iPad lu&ocirc;n mang tới cho người d&ugrave;ng thời gian sử dụng vượt trội so với c&aacute;c đối thủ của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd6.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Thời lượng pin vượt trội\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd6.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Thời lượng pin vượt trội\" /></a></p>\r\n\r\n<p>Với chiếc iPad n&agrave;y th&igrave; bạn ho&agrave;n to&agrave;n c&oacute; thể thoải m&aacute;i sử dụng trong khoảng 2-3 ng&agrave;y mới phải sạc pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd4.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Giao diện hệ điều hành iPad OS\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd4.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Giao diện hệ điều hành iPad OS\" /></a></p>\r\n\r\n<p>Việc onscreen li&ecirc;n tục được khoảng 10 tiếng l&agrave; một điều hết thức b&igrave;nh thường với chiếc m&aacute;y n&agrave;y v&agrave; nếu bạn chỉ d&ugrave;ng để xem phim th&igrave; n&oacute; c&ograve;n ấn tượng hơn nhiều.</p>\r\n\r\n<h3>Hiệu năng mượt m&agrave; v&agrave; ổn định</h3>\r\n\r\n<p>Tuy kh&ocirc;ng c&oacute; sức mạnh ấn tượng như những chiếc iPad Pro nhưng hiệu năng m&agrave; m&aacute;y mang lại vẫn đủ l&agrave;m h&agrave;i l&ograve;ng phần lớn người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd2-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Hiệu năng mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd2-1.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Hiệu năng mạnh mẽ\" /></a></p>\r\n\r\n<p>Cung cấp sức mạnh cho m&aacute;y l&agrave; con chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tong-quan-ve-chip-a10-fusion-cua-apple-885052\" target=\"_blank\" title=\"Tìm hiểu chip Apple A10 Fusion\">Apple A10 Fusion</a>&nbsp;kết hợp với 3 GB RAM v&agrave; 32 GB bộ nhớ trong.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd11-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Trải nghiếm sử dụng mượt mà\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd11-1.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Trải nghiếm sử dụng mượt mà\" /></a></p>\r\n\r\n<p>M&aacute;y hoạt động mượt m&agrave; v&agrave; ổn định với hầu hết c&aacute;c thao t&aacute;c hằng ng&agrave;y của người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd10.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Cổng kết nối Lightning\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd10.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Cổng kết nối Lightning\" /></a></p>\r\n\r\n<p>Bạn cũng c&oacute; thể sử dụng chiếc m&aacute;y n&agrave;y để chơi game v&agrave; sẽ c&oacute; một trải nghiệm m&agrave; kh&ocirc;ng một chiếc smartphone n&agrave;o c&oacute; được.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd12.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Trải nghiệm sử dụng như một chiếc laptop\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd12.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Trải nghiệm sử dụng như một chiếc laptop\" /></a></p>\r\n\r\n<p>Thao t&aacute;c cử chỉ vuốt đơn giản, dễ sử dụng gi&uacute;p bạn tiết kiệm được nhiều thời gian.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd5-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Cảm biến vân tay Touch ID\" src=\"https://cdn.tgdd.vn/Products/Images/522/213099/ipad-10-2-inch-wifi-cellular-32gb-2019-tgdd5-1.jpg\" title=\"Điện thoại iPad 10.2 inch Wifi Cellular 32GB (2019) | Cảm biến vân tay Touch ID\" /></a></p>\r\n\r\n<p>Cảm biến v&acirc;n tay cũng được duy tr&igrave; gi&uacute;p bạn mở kh&oacute;a thiết bị một c&aacute;ch dễ d&agrave;ng.</p>', 3, 0, 3, 14),
(33, 'Máy tính bảng iPad Pro 11 inch Wifi 64GB', 'may-tinh-bang-ipad-pro-11-inch-wifi-64gb', 1, 21990000, 0, 0, 1, 0, 0, 'Máy tính bảng iPad Pro 11 inch Wifi 64GB', '2019-11-12__ipad-pro-11-inch-2018-64gb-wifi-33397-chitiet-400x460.png', 'Máy tính bảng iPad Pro 11 inch Wifi 64GB', NULL, '2019-11-12 08:17:24', '2019-11-13 08:19:35', 'Máy tính bảng iPad Pro 11 inch Wifi 64GB', '<p>N&uacute;t Home tr&ecirc;n thế hệ mới đ&atilde; được loại bỏ, c&aacute;c thao t&aacute;c về lại m&agrave;n h&igrave;nh ch&iacute;nh, mở thanh th&ocirc;ng b&aacute;o, mở đa nhiệm, bảng điều khiển,... đều c&oacute; thể sử dụng dễ d&agrave;ng th&ocirc;ng qua c&aacute;c thao t&aacute;c k&eacute;o vuốt thả tr&ecirc;n m&agrave;n h&igrave;nh ch&iacute;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-thietke.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế tinh tế trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-thietke.jpg\" title=\"Thiết kế tinh tế trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh si&ecirc;u sắc n&eacute;t</h3>\r\n\r\n<p>C&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;Liquid&nbsp;Retina với độ ph&acirc;n giải 2388 x 1668 pixels tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng chính hãng đang kinh doanh tại Thegioididong.com\">m&aacute;y t&iacute;nh bảng</a>&nbsp;11 inch c&oacute; khả&nbsp;năng hiển thị sắc n&eacute;t đến từng chi tiết nhỏ, m&agrave;u sắc c&oacute; độ s&acirc;u v&agrave; ch&acirc;n thật.&nbsp;Xem phim, chơi game,... đều cho trải nghiệm tuyệt vời nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sắc nét trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-manhinh.jpg\" title=\"Màn hình sắc nét trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<h3>Vi xử l&yacute; mạnh mẽ</h3>\r\n\r\n<p>Vi xử l&yacute;&nbsp;<strong>Apple A12X Bionic</strong>&nbsp;mạnh mẽ kết hợp c&ugrave;ng&nbsp;<strong>4GB RAM&nbsp;</strong>v&agrave;&nbsp;<strong>hệ điều h&agrave;nh IOS 12</strong>&nbsp;gi&uacute;p<strong>&nbsp;iPad Pro 11 inch 64GB Wifi (2018)</strong>&nbsp;c&oacute; thể chiến mượt m&agrave; c&aacute;c tựa game nặng nhất hiện tại v&agrave; chạy trơn tru c&aacute;c ứng dụng đồ hoạ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-tgdd-.jpg\" onclick=\"return false;\"><img alt=\"Điểm hiệu năng Antutu Benchmark trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-tgdd-.jpg\" title=\"Điểm hiệu năng Antutu Benchmark trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<p>Với&nbsp;Apple A12X Bionic l&agrave; trung t&acirc;m xử l&yacute;,&nbsp;iPad Pro 11 inch 64GB Wifi (2018)&nbsp;ngo&agrave;i giải tr&iacute; c&ograve;n đ&aacute;p ứng tốt nhu cầu xử l&yacute; c&ocirc;ng việc hằng ng&agrave;y của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-dmx-cauhinh.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình mạnh mẽ trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-dmx-cauhinh.jpg\" title=\"Cấu hình mạnh mẽ trên Cổng kết nối USB C đa chức năng trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>', 0, 0, 0, 0),
(34, 'Laptop Asus VivoBook A412F', 'laptop-asus-vivobook-a412f', 9, 13490000, 0, 0, 1, 0, 0, 'Laptop Asus VivoBook A412F', '2019-11-12__asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win10-600x600.jpg', 'Laptop Asus VivoBook A412F', NULL, '2019-11-12 08:19:46', '2019-11-13 00:55:11', 'Laptop Asus VivoBook A412F', '<h3>Thiết kế mỏng nhẹ, năng động</h3>\r\n\r\n<p>Mỏng nhẹ chỉ&nbsp;<strong>19 mm - 1.5 kg</strong>, chỉ xấp xỉ v&agrave;i quyển s&aacute;ch,&nbsp;<a href=\"https://www.thegioididong.com/laptop-asus-vivobook\" target=\"_blank\" title=\"Xem thêm các sản phẩm Asus VivoBook đang bán tại Thegioididong.com\" type=\"Xem thêm các sản phẩm Asus VivoBook đang bán tại Thegioididong.com\">ASUS VivoBook</a>&nbsp;A412F ph&ugrave; hợp với giới trẻ, những người cần đem laptop theo thường xuy&ecirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win10-5-1.jpg\" onclick=\"return false;\"><img alt=\"ASUS VivoBook A412F i3 có thiết kế mỏng nhẹ\" src=\"https://cdn.tgdd.vn/Products/Images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win10-5-1.jpg\" title=\"ASUS VivoBook A412F i3 có thiết kế mỏng nhẹ\" /></a></p>\r\n\r\n<p>M&aacute;y c&oacute; kiểu d&aacute;ng trẻ trung, m&agrave;u sắc sang trọng đẹp mắt.&nbsp;</p>\r\n\r\n<h3>D&ugrave;ng văn ph&ograve;ng v&agrave; đồ họa cơ bản</h3>\r\n\r\n<p>C&ocirc;ng nghệ&nbsp;<strong>CPU Intel Core i3&nbsp;</strong>thế hệ thứ 8 cho hiệu năng đủ d&ugrave;ng, c&aacute;c ứng dụng như Word, Excel, Photoshop cơ bản đều c&oacute; thể chạy tốt.&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\" type=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\"><strong>RAM</strong>&nbsp;<strong>4 GB</strong></a>&nbsp;hỗ trợ đa nhiệm ở mức kh&aacute;, mở 15-20 tab Chrome kh&ocirc;ng sợ giật, đơ m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win101.jpg\" onclick=\"return false;\"><img alt=\"Laptop ASUS VivoBook A412 i3 sử dụng văn phòng, đồ họa cơ bản\" src=\"https://cdn.tgdd.vn/Products/Images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win101.jpg\" title=\"Laptop ASUS VivoBook A412 i3 sử dụng văn phòng, đồ họa cơ bản\" /></a></p>\r\n\r\n<h3>Nhanh hơn cả SSD th&ocirc;ng thường</h3>\r\n\r\n<p>ASUS VivoBook vừa trang bị&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?f=o-cung-ssd\" target=\"_blank\" title=\"Laptop chính hãng được trang bị ổ cứng SSD đang được kinh doanh tại Thegioididong.com\" type=\"Laptop chính hãng được trang bị ổ cứng SSD đang được kinh doanh tại Thegioididong.com\">ổ cứng SSD</a>&nbsp;512 GB</strong>&nbsp;vừa c&oacute; ổ cứng&nbsp;<strong>Intel Optane 32 GB</strong>&nbsp;tối ưu tốc độ vận h&agrave;nh. Khởi động m&aacute;y v&agrave; ứng dụng chỉ v&agrave;i gi&acirc;y - dung lượng lưu trữ rộng.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win10-6-1.jpg\" onclick=\"return false;\"><img alt=\"Ổ cứng SSD cực nhanh - ASUS VivoBook A412F\" src=\"https://cdn.tgdd.vn/Products/Images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win10-6-1.jpg\" title=\"Ổ cứng SSD cực nhanh - ASUS VivoBook A412F\" /></a></p>\r\n\r\n<h3>Bản lề Ergolift s&aacute;ng tạo</h3>\r\n\r\n<p>Bản lề n&acirc;ng g&oacute;c m&aacute;y l&ecirc;n một g&oacute;c 2 độ hạn chế trầy xước, tr&oacute;c lớp sơn của mặt đ&aacute;y laptop đồng thời cho trải nghiệm g&otilde; ph&iacute;m &ecirc;m &aacute;i v&agrave; tản nhiệt hiệu quả.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win102.jpg\" onclick=\"return false;\"><img alt=\"Laptop ASUS VivoBook A412 i3 có bản lề Ergolift độc đáo\" src=\"https://cdn.tgdd.vn/Products/Images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win102.jpg\" title=\"Laptop ASUS VivoBook A412 i3 có bản lề Ergolift độc đáo\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh tr&agrave;n viền nh&igrave;n cực đ&atilde; mắt</h3>\r\n\r\n<p>Viền m&agrave;n h&igrave;nh si&ecirc;u mỏng đem đến kh&ocirc;ng gian nh&igrave;n rộng hơn, cảm gi&aacute;c nh&igrave;n th&iacute;ch mắt hơn so với<a href=\"https://www.thegioididong.com/laptop-14-inch\" target=\"_blank\" title=\"Xem thêm một số laptop màn hình 14 inch đang được kinh doanh tại Thegioididong.com\" type=\"Xem thêm một số laptop màn hình 14 inch đang được kinh doanh tại Thegioididong.com\">&nbsp;laptop 14 inch</a>&nbsp;th&ocirc;ng thường. Độ ph&acirc;n giải&nbsp;<strong>Full HD (1920 x 1080)</strong>&nbsp;cho h&igrave;nh ảnh mịn m&agrave;ng, sắc n&eacute;t. C&ocirc;ng nghệ chống ch&oacute;i giảm t&igrave;nh trạng l&oacute;a m&agrave;n h&igrave;nh khi d&ugrave;ng ngược s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win10-31.jpg\" onclick=\"return false;\"><img alt=\"Laptop ASUS VivoBook A412 i3 có màn hình 14 inch Full HD\" src=\"https://cdn.tgdd.vn/Products/Images/44/212373/asus-vivobook-a412f-i3-8145u-4gb-32gb-512gb-win10-31.jpg\" title=\"Laptop ASUS VivoBook A412 i3 có màn hình 14 inch Full HD\" /></a></p>', 0, 10, 0, 0),
(35, 'Laptop Acer Swift 3 SF315', 'laptop-acer-swift-3-sf315', 9, 11490000, 0, 0, 1, 0, 0, 'Laptop Acer Swift 3 SF315', '2019-11-12__acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-600x600.jpg', 'Laptop Acer Swift 3 SF315', NULL, '2019-11-12 08:23:26', '2019-11-13 00:54:03', 'Laptop Acer Swift 3 SF315', '<h3>Vỏ kim loại nguy&ecirc;n khối cao cấp</h3>\r\n\r\n<p>Thu h&uacute;t &aacute;nh mắt người d&ugrave;ng ngay từ lần đầu ti&ecirc;n,&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các sản phẩm laptop đang bán tại Thegioididong.com\" type=\"Xem thêm các sản phẩm laptop đang bán tại Thegioididong.com\">laptop</a>&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer-swift\" target=\"_blank\" title=\"Xem thêm các sản phẩm Acer Swift đang bán tại Thegioididong.com\" type=\"Xem thêm các sản phẩm Acer Swift đang bán tại Thegioididong.com\">Acer Swift</a>&nbsp;SF315 sở hữu bộ khung vỏ kim loại nguy&ecirc;n khối sang trọng, chắc chắn. Một ưu điểm của chiếc m&aacute;y nữa đ&oacute; l&agrave; độ mỏng&nbsp;<strong>16.9 mm</strong>&nbsp;v&agrave; trọng lượng chỉ vỏn vẹn&nbsp;<strong>1.7 kg</strong>, người d&ugrave;ng dễ d&agrave;ng mang theo chiếc m&aacute;y khi đi học hay đi l&agrave;m.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-7-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế gọn nhẹ của laptop Acer Swift SF315 52 38YQ i3\" src=\"https://cdn.tgdd.vn/Products/Images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-7-1.jpg\" title=\"Thiết kế gọn nhẹ của laptop Acer Swift SF315 52 38YQ i3\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh sử dụng văn ph&ograve;ng mượt m&agrave;</h3>\r\n\r\n<p>Với bộ xử l&yacute;&nbsp;<strong>Intel&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=core-i3\" target=\"_blank\" title=\"Xem thêm các laptop Core i3 đang bán tại Thegioididong.com\" type=\"Xem thêm các laptop Core i3 đang bán tại Thegioididong.com\">Core i3</a>&nbsp;8130U</strong>&nbsp;c&ugrave;ng bộ nhớ&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\" type=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\"><strong>RAM 4 GB</strong></a>, chiếc m&aacute;y c&oacute; thể giải quyết mượt m&agrave; c&aacute;c t&aacute;c vụ văn ph&ograve;ng cơ bản như soạn thảo văn bản trong Word, l&agrave;m việc với Excel, PowerPoint hay chỉnh sửa h&igrave;nh ảnh đơn giản trong Photoshop,...&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-1-1.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình sử dụng văn phòng mượt mà của laptop Acer Swift SF315 52 38YQ i3\" src=\"https://cdn.tgdd.vn/Products/Images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-1-1.jpg\" title=\"Cấu hình sử dụng văn phòng mượt mà của laptop Acer Swift SF315 52 38YQ i3\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop?g=hdd-tu-500-gb-den-1-tb\" target=\"_blank\" title=\"Laptop được trang bị ổ cứng HDD đang kinh doanh tại Thegioidiong.com\" type=\"Laptop được trang bị ổ cứng HDD đang kinh doanh tại Thegioidiong.com\">Ổ cứng&nbsp;</a><strong><a href=\"https://www.thegioididong.com/laptop?g=hdd-tu-500-gb-den-1-tb\" target=\"_blank\" title=\"Laptop được trang bị ổ cứng HDD đang kinh doanh tại Thegioidiong.com\" type=\"Laptop được trang bị ổ cứng HDD đang kinh doanh tại Thegioidiong.com\">HDD</a>&nbsp;1TB</strong>&nbsp;với dung lượng lưu trữ cực lớn gi&uacute;p bạn thoải m&aacute;i lưu trữ h&igrave;nh ảnh, t&agrave;i liệu hay những bộ phim, b&agrave;i h&aacute;t y&ecirc;u th&iacute;ch.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-2-1.jpg\" onclick=\"return false;\"><img alt=\"Ổ cứng của laptop Acer Swift SF315 52 38YQ i3\" src=\"https://cdn.tgdd.vn/Products/Images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-2-1.jpg\" title=\"Ổ cứng của laptop Acer Swift SF315 52 38YQ i3\" /></a></p>\r\n\r\n<h3>Acer Swift 3 c&oacute; thời lượng pin l&ecirc;n đến 11 giờ</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-21.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin laptop Acer Swift SF315 52 38YQ i3\" src=\"https://cdn.tgdd.vn/Products/Images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-21.jpg\" title=\"Thời lượng pin laptop Acer Swift SF315 52 38YQ i3\" /></a></p>\r\n\r\n<h3>H&igrave;nh ảnh sắc n&eacute;t, g&oacute;c nh&igrave;n si&ecirc;u rộng</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh rộng&nbsp;<a href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" title=\"Xem thêm một số laptop màn hình trên 15 inch đang được kinh doanh tại Thegioididong.com\" type=\"Xem thêm một số laptop màn hình trên 15 inch đang được kinh doanh tại Thegioididong.com\"><strong>15.6 inch</strong></a>&nbsp;c&ugrave;ng độ ph&acirc;n giải&nbsp;<strong>Full HD</strong>&nbsp;mang đến chất lượng h&igrave;nh ảnh ch&acirc;n thực, sắc n&eacute;t.&nbsp;</p>\r\n\r\n<p>C&ocirc;ng nghệ<strong>&nbsp;Acer ComfyView</strong>&nbsp;tăng khả năng chống ch&oacute;i c&ugrave;ng tấm nền<strong>&nbsp;IPS</strong>&nbsp;mang đến g&oacute;c nh&igrave;n rộng, độ tương phản cao, m&agrave;u sắc trung thực gi&uacute;p bạn c&oacute; những trải nghiệm h&igrave;nh ảnh tuyệt vời nhất.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-6-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình laptop Acer Swift SF315 52 38YQ i3\" src=\"https://cdn.tgdd.vn/Products/Images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-6-1.jpg\" title=\"Màn hình laptop Acer Swift SF315 52 38YQ i3\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-acer\" target=\"_blank\" title=\"Xem thêm các sản phẩm laptop Acer đang bán tại Thegioididong.com\" type=\"Xem thêm các sản phẩm laptop Acer đang bán tại Thegioididong.com\">Laptop Acer</a>&nbsp;c&ograve;n được trang bị đ&egrave;n b&agrave;n ph&iacute;m gi&uacute;p bạn&nbsp;<strong>dễ d&agrave;ng sử dụng cả trong b&oacute;ng tối</strong>.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-4-1.jpg\" onclick=\"return false;\"><img alt=\"Đèn bàn phím của laptop Acer Swift SF315 52 38YQ i3\" src=\"https://cdn.tgdd.vn/Products/Images/44/208863/acer-swift-sf315-52-38yq-i3-8130u-4gb-1tb-156f-win-4-1.jpg\" title=\"Đèn bàn phím của laptop Acer Swift SF315 52 38YQ i3\" /></a></p>\r\n\r\n<p>Bộ cảm biến v&acirc;n tay được đặt dưới b&agrave;n ph&iacute;m kết hợp với t&iacute;nh năng Windows Hello gi&uacute;p người d&ugrave;ng dễ d&agrave;ng mở kh&oacute;a&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\" type=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">m&aacute;y t&iacute;nh</a>&nbsp;với thao t&aacute;c một chạm c&ugrave;ng độ&nbsp;<strong>bảo mật</strong>&nbsp;v&agrave;&nbsp;<strong>an to&agrave;n</strong>&nbsp;cao.&nbsp;</p>', 0, 15, 0, 0);
